'use client';

import { useState, useEffect } from 'react';
import { useRouter } from 'next/navigation';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { ArrowLeft, Loader2, LogIn, Shield, Clock, User } from 'lucide-react';
import Link from 'next/link';

export const dynamic = 'force-dynamic';

export default function AccessCodePage() {
  const [accessCode, setAccessCode] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');

  const [showForgotCode, setShowForgotCode] = useState(false);
  const [forgotCodeEmail, setForgotCodeEmail] = useState('');
  const [forgotCodeLoading, setForgotCodeLoading] = useState(false);
  const [forgotCodeSuccess, setForgotCodeSuccess] = useState(false);
  const router = useRouter();

  // Handle autofill detection
  useEffect(() => {
    const checkAutofill = () => {
      const input = document.getElementById('accessCode') as HTMLInputElement;
      if (input && input.value && !accessCode) {
        setAccessCode(input.value);
      }
    };

    // Check immediately and after a short delay for autofill
    checkAutofill();
    const timeoutId = setTimeout(checkAutofill, 100);

    // Also check on animation frame for better detection
    const rafId = requestAnimationFrame(checkAutofill);

    return () => {
      clearTimeout(timeoutId);
      cancelAnimationFrame(rafId);
    };
  }, []);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!accessCode.trim()) return;

    setIsLoading(true);
    setError('');

    try {
      const response = await fetch('/api/auth/verify-code', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ 
          accessCode: accessCode.trim().toUpperCase() 
        }),
      });

      const data = await response.json();

      if (response.ok && data.success) {
        localStorage.setItem(
          'paintquote_company',
          JSON.stringify({
            id: data.company.id,
            accessCode: data.company.accessCode,
            name: data.company.name,
            phone: data.company.phone || '',
            email: data.company.email || '',
            logoUrl: data.company.logoUrl || null,
            loginTime: Date.now(),
            isNewCompany: data.company.needsOnboarding || false,
            onboarding_completed: data.company.onboarding_completed || false
          })
        );
        // Use window.location for a hard redirect to ensure proper loading
        window.location.href = '/dashboard';
      } else {
        console.error('Login failed:', data);
        setError(data.error || 'Invalid access code. Please try again.');
      }
    } catch (error) {
      setError('An error occurred. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };


  const handleForgotCode = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!forgotCodeEmail.trim()) return;

    setForgotCodeLoading(true);
    try {
      const response = await fetch('/api/auth/resend-code', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email: forgotCodeEmail.trim() }),
      });

      const data = await response.json();

      if (response.ok) {
        setForgotCodeSuccess(true);
      } else {
        setError(data.error || 'Failed to send code. Please try again.');
      }
    } catch (error) {
      setError('An error occurred. Please try again.');
    } finally {
      setForgotCodeLoading(false);
    }
  };



  return (
    <div className="min-h-screen gradient-animate">
      <div className="container max-w-lg mx-auto px-4 py-16">
        <div className="mb-8">
          <Link 
            href="/" 
            className="inline-flex items-center text-sm text-gray-600 hover:text-gray-900 transition-colors"
          >
            <ArrowLeft className="h-4 w-4 mr-1" />
            Back to home
          </Link>
        </div>

        <Card className="glass-card bg-surface border-white/20">
          <CardHeader className="text-center pb-8">
            <div className="mx-auto w-16 h-16 bg-gradient-to-br from-blue-400 to-blue-600 rounded-full flex items-center justify-center mb-4 shadow-lg shadow-blue-500/30">
              <LogIn className="h-8 w-8 text-white" />
            </div>
            <CardTitle className="text-3xl font-bold text-gradient-modern">
              Welcome Back
            </CardTitle>
            <CardDescription className="text-base mt-2 text-medium-contrast">
              Sign in with your company access code
            </CardDescription>
          </CardHeader>

          <CardContent className="space-y-6">
            <form onSubmit={handleSubmit} className="space-y-4">
              <div className="space-y-2">
                <Label htmlFor="accessCode" className="text-base font-medium text-high-contrast">
                  Access Code
                </Label>
                <Input
                  id="accessCode"
                  name="accessCode"
                  type="text"
                  placeholder="Enter your access code"
                  value={accessCode}
                  onChange={(e) => {
                    const newValue = e.target.value;
                    setAccessCode(newValue);
                  }}
                  onInput={(e) => {
                    // Fallback for cases where onChange doesn't fire
                    const target = e.target as HTMLInputElement;
                    setAccessCode(target.value);
                  }}
                  autoComplete="one-time-code"
                  autoCapitalize="characters"
                  spellCheck={false}
                  className="h-12 text-base font-mono uppercase input-modern"
                  style={{ textTransform: 'uppercase' }}
                  disabled={isLoading}
                  required
                />
                <div className="flex items-center justify-between">
                  <p className="text-sm text-gray-500">
                    Your unique code provided during registration
                  </p>
                  <button
                    type="button"
                    onClick={() => setShowForgotCode(true)}
                    className="text-sm text-blue-600 hover:text-blue-700 hover:underline transition-colors"
                  >
                    Forgot your code?
                  </button>
                </div>
              </div>

              {error && (
                <Alert variant="destructive">
                  <AlertDescription>{error}</AlertDescription>
                </Alert>
              )}

              <Button
                type="submit"
                className="w-full h-12 text-base font-medium btn-primary-modern"
                disabled={isLoading || !accessCode || accessCode.trim().length === 0}
              >
                {isLoading ? (
                  <>
                    <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                    Signing in...
                  </>
                ) : (
                  'Sign In'
                )}
              </Button>
            </form>



            <div className="text-center pt-4 border-t">
              <p className="text-sm text-gray-600">
                Don't have an account?{' '}
                <Link href="/trial-signup" className="font-medium text-blue-600 hover:underline">
                  Start free trial
                </Link>
              </p>
            </div>
          </CardContent>
        </Card>

        {/* Features reminder */}
        <div className="mt-8 grid grid-cols-3 gap-4 text-center">
          <div className="space-y-2">
            <div className="mx-auto w-10 h-10 glass-card rounded-full flex items-center justify-center">
              <Shield className="h-5 w-5 text-blue-400" />
            </div>
            <p className="text-xs text-medium-contrast">Secure Access</p>
          </div>
          <div className="space-y-2">
            <div className="mx-auto w-10 h-10 glass-card rounded-full flex items-center justify-center">
              <Clock className="h-5 w-5 text-emerald-400" />
            </div>
            <p className="text-xs text-medium-contrast">Quick Login</p>
          </div>
          <div className="space-y-2">
            <div className="mx-auto w-10 h-10 glass-card rounded-full flex items-center justify-center">
              <User className="h-5 w-5 text-purple-400" />
            </div>
            <p className="text-xs text-medium-contrast">Multi-Company</p>
          </div>
        </div>

        {/* Forgot Code Modal */}
        <Dialog open={showForgotCode} onOpenChange={setShowForgotCode}>
          <DialogContent className="glass-card bg-surface border-white/20">
            <DialogHeader>
              <DialogTitle className="text-xl font-bold text-gradient-modern">
                Forgot Your Code?
              </DialogTitle>
              <DialogDescription className="text-medium-contrast">
                Enter your email address and we'll send your access code
              </DialogDescription>
            </DialogHeader>

            {forgotCodeSuccess ? (
              <div className="space-y-4">
                <Alert className="border-green-500/50 bg-green-500/10">
                  <AlertDescription className="text-green-400">
                    Access code sent! Check your email for your code.
                  </AlertDescription>
                </Alert>
                <Button
                  onClick={() => {
                    setShowForgotCode(false);
                    setForgotCodeSuccess(false);
                    setForgotCodeEmail('');
                  }}
                  className="w-full btn-primary-modern"
                >
                  Close
                </Button>
              </div>
            ) : (
              <form onSubmit={handleForgotCode} className="space-y-4">
                <div className="space-y-2">
                  <Label htmlFor="forgotEmail" className="text-base font-medium text-high-contrast">
                    Email Address
                  </Label>
                  <Input
                    id="forgotEmail"
                    type="email"
                    placeholder="Enter your email address"
                    value={forgotCodeEmail}
                    onChange={(e) => setForgotCodeEmail(e.target.value)}
                    className="h-12 text-base input-modern"
                    disabled={forgotCodeLoading}
                    required
                  />
                </div>

                <div className="flex gap-3">
                  <Button
                    type="button"
                    variant="outline"
                    onClick={() => {
                      setShowForgotCode(false);
                      setForgotCodeEmail('');
                      setError('');
                    }}
                    className="flex-1"
                    disabled={forgotCodeLoading}
                  >
                    Cancel
                  </Button>
                  <Button
                    type="submit"
                    className="flex-1 btn-primary-modern"
                    disabled={forgotCodeLoading || !forgotCodeEmail.trim()}
                  >
                    {forgotCodeLoading ? (
                      <>
                        <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                        Sending...
                      </>
                    ) : (
                      'Send Code'
                    )}
                  </Button>
                </div>
              </form>
            )}
          </DialogContent>
        </Dialog>
      </div>
    </div>
  );
}

import { NextRequest, NextResponse } from 'next/server';
import { getAuthContext } from '@/lib/auth/middleware';
import { quoteAssistant } from '@/lib/ai/quote-assistant';
import { calculator, QuoteCalculator } from '@/lib/calculators/quote-calculator';
import { db } from '@/lib/database/adapter';
import { generateQuoteNumber } from '@/lib/quote-number-generator';

export const dynamic = 'force-dynamic';

export async function POST(request: NextRequest) {
  try {
    // Verify authentication
    const auth = await getAuthContext(request);
    if (!auth || auth.type !== 'company') {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    const { conversationHistory, context, surfaces } = await request.json();

    if (!conversationHistory && !context) {
      return NextResponse.json(
        { error: 'Conversation history or context is required' },
        { status: 400 }
      );
    }

    // Parse quote information from conversation
    let quoteContext = context || {};

    if (conversationHistory && Array.isArray(conversationHistory)) {
      const conversationText = conversationHistory
        .map(msg => `${msg.role}: ${msg.content}`)
        .join('\n');

      const parsedInfo = await quoteAssistant.parseQuoteInformation(conversationText);
      quoteContext = { ...quoteContext, ...parsedInfo };
    }

    // Ensure we have minimum required information
    if (!quoteContext.customerName) {
      return NextResponse.json(
        { error: 'Customer name is required' },
        { status: 400 }
      );
    }

    // Calculate quote pricing
    let calculatorInput;

    if (surfaces) {
      // Use provided surfaces
      calculatorInput = {
        surfaces,
        paintQuality: quoteContext.paintQuality || 'better'
      };
    } else if (quoteContext.rooms && quoteContext.rooms.length > 0) {
      // Estimate from rooms
      calculatorInput = QuoteCalculator.estimateFromRooms(
        quoteContext.rooms,
        quoteContext.paintQuality
      );
    } else if (quoteContext.measurements) {
      // Use direct measurements
      calculatorInput = {
        surfaces: {
          walls: quoteContext.measurements.wallSqft,
          ceilings: quoteContext.measurements.ceilingSqft,
          trim: quoteContext.measurements.trimLinearFt,
          doors: quoteContext.measurements.doors,
          windows: quoteContext.measurements.windows
        },
        paintQuality: quoteContext.paintQuality || 'better'
      };
    } else {
      return NextResponse.json(
        { error: 'Surface measurements or room dimensions are required' },
        { status: 400 }
      );
    }

    // Get company settings for markup and tax, and check subscription
    const company = await db.query(
      'SELECT default_labor_percentage, tax_rate, subscription_tier, monthly_quote_count, monthly_quote_limit FROM companies WHERE id = ?',
      [auth.company!.id]
    );

    // Check if company has reached their quote limit (for free tier)
    if (company.length > 0) {
      const companyData = company[0];
      if (companyData.subscription_tier === 'free' && 
          companyData.monthly_quote_limit > 0 && 
          companyData.monthly_quote_count >= companyData.monthly_quote_limit) {
        return NextResponse.json({
          error: 'Quote limit reached',
          requiresUpgrade: true,
          upgradeUrl: process.env.NEXT_PUBLIC_STRIPE_PRO_MONTHLY_LINK || '/dashboard/settings/billing'
        }, { status: 403 });
      }
    }

    if (company.length > 0 && 'markupPercentage' in calculatorInput) {
      calculatorInput.markupPercentage = company[0].default_labor_percentage || 30;
      calculatorInput.taxRate = company[0].tax_rate || 0;
    }

    // Calculate the quote
    const calculation = QuoteCalculator.calculate(calculatorInput);

    // Generate quote summary
    const summary = await quoteAssistant.generateQuoteSummary(
      quoteContext,
      calculation
    );

    // Create the quote
    const quoteId = await generateQuoteNumber(auth.company!.id);

    const quote = {
      company_id: auth.company!.id,
      quote_id: quoteId,
      customer_name: quoteContext.customerName,
      customer_email: quoteContext.customerEmail || null,
      customer_phone: quoteContext.customerPhone || null,
      address: quoteContext.address || null,
      project_type: quoteContext.projectType || 'interior',
      rooms: JSON.stringify(quoteContext.rooms || []),
      room_count: quoteContext.rooms?.length || 0,
      paint_quality: quoteContext.paintQuality || 'better',
      prep_work: quoteContext.prepWork || null,
      timeline: quoteContext.timeline || null,
      special_requests: quoteContext.specialRequests || null,
      walls_sqft: calculatorInput.surfaces.walls || 0,
      ceilings_sqft: calculatorInput.surfaces.ceilings || 0,
      trim_sqft: calculatorInput.surfaces.trim || 0,
      doors_count: calculatorInput.surfaces.doors || 0,
      windows_count: calculatorInput.surfaces.windows || 0,
      total_revenue: calculation.total,
      total_materials: calculation.materials.total,
      projected_labor: calculation.labor.total,
      base_cost: calculation.subtotal,
      markup_percentage: calculatorInput.markupPercentage || 30,
      final_price: calculation.total,
      conversation_summary: JSON.stringify(conversationHistory || []),
      status: 'draft'
    };

    // Save to database
    const result = await db.createQuote(quote);

    return NextResponse.json({
      success: true,
      quoteId,
      quote: {
        ...quote,
        id: result.id,
        summary,
        calculation
      }
    });

  } catch (error) {
    console.error('Error creating quote from conversation:', error);
    return NextResponse.json(
      { error: 'Failed to create quote' },
      { status: 500 }
    );
  }
}

import { NextRequest, NextResponse } from 'next/server';
import { getAuthContext } from '@/lib/auth/middleware';
import { quoteAssistant } from '@/lib/ai/quote-assistant';

export async function POST(request: NextRequest) {
  try {
    // Verify authentication
    const auth = await getAuthContext(request);
    if (!auth) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    const { text, conversationHistory } = await request.json();

    if (!text && !conversationHistory) {
      return NextResponse.json(
        { error: 'Text or conversation history is required' },
        { status: 400 }
      );
    }

    // Build conversation text
    let conversationText = text || '';

    if (conversationHistory && Array.isArray(conversationHistory)) {
      conversationText = conversationHistory
        .map(msg => `${msg.role}: ${msg.content}`)
        .join('\n');
    }

    // Parse the quote information
    const parsedInfo = await quoteAssistant.parseQuoteInformation(conversationText);

    // Validate and clean parsed data
    const cleanedInfo = {
      customerName: parsedInfo.customerName || null,
      customerEmail: parsedInfo.customerEmail || null,
      customerPhone: parsedInfo.customerPhone || null,
      address: parsedInfo.address || null,
      projectType: parsedInfo.projectType || 'interior',
      surfaces: Array.isArray(parsedInfo.surfaces) ? parsedInfo.surfaces : [],
      rooms: Array.isArray(parsedInfo.rooms) ? parsedInfo.rooms : [],
      measurements: {
        wallSqft: parsedInfo.measurements?.wallSqft || 0,
        ceilingSqft: parsedInfo.measurements?.ceilingSqft || 0,
        trimLinearFt: parsedInfo.measurements?.trimLinearFt || 0,
        doors: parsedInfo.measurements?.doors || 0,
        windows: parsedInfo.measurements?.windows || 0
      },
      paintQuality: parsedInfo.paintQuality || null,
      prepWork: parsedInfo.prepWork || null,
      timeline: parsedInfo.timeline || null,
      specialRequests: parsedInfo.specialRequests || null
    };

    // Calculate confidence score based on completeness
    const requiredFields = ['customerName', 'address', 'projectType'];
    const importantFields = ['surfaces', 'measurements.wallSqft'];

    let score = 0;
    let maxScore = 0;

    requiredFields.forEach(field => {
      maxScore += 2;
      if (cleanedInfo[field as keyof typeof cleanedInfo]) score += 2;
    });

    if (cleanedInfo.surfaces.length > 0) score += 1;
    if (cleanedInfo.measurements.wallSqft > 0) score += 1;
    maxScore += 2;

    const confidence = Math.round((score / maxScore) * 100);

    return NextResponse.json({
      success: true,
      parsedData: cleanedInfo,
      confidence,
      isComplete: confidence >= 70
    });

  } catch (error) {
    console.error('Quote parsing error:', error);
    return NextResponse.json(
      { error: 'Failed to parse quote information' },
      { status: 500 }
    );
  }
}

import { NextRequest, NextResponse } from 'next/server';
import { getAuthContext } from '@/lib/auth/middleware';
import { quoteAssistant } from '@/lib/ai/quote-assistant';
import { db } from '@/lib/database/adapter';

export async function POST(request: NextRequest) {
  try {
    // Verify authentication
    const auth = await getAuthContext(request);
    if (!auth || auth.type !== 'company') {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    const { message, context, conversationHistory } = await request.json();

    if (!message) {
      return NextResponse.json(
        { error: 'Message is required' },
        { status: 400 }
      );
    }

    // Add company ID to context
    const enhancedContext = {
      ...context,
      companyId: auth.company!.id
    };

    // Process the message
    const response = await quoteAssistant.processMessage(
      message,
      enhancedContext,
      conversationHistory || []
    );

    // Detect conversation stage
    const stage = await quoteAssistant.detectConversationStage(
      conversationHistory || [],
      enhancedContext
    );

    // Try to parse any new information from the conversation
    const allMessages = [
      ...(conversationHistory || []),
      { role: 'user', content: message },
      { role: 'assistant', content: response }
    ];

    const conversationText = allMessages
      .map(msg => `${msg.role}: ${msg.content}`)
      .join('\n');

    const parsedInfo = await quoteAssistant.parseQuoteInformation(conversationText);

    return NextResponse.json({
      response,
      stage,
      parsedContext: {
        ...enhancedContext,
        ...parsedInfo
      }
    });

  } catch (error) {
    console.error('Quote assistant error:', error);
    return NextResponse.json(
      { error: 'Failed to process message' },
      { status: 500 }
    );
  }
}

// GET endpoint to check API status
export async function GET() {
  const hasApiKey = !!process.env.OPENROUTER_API_KEY;

  return NextResponse.json({
    status: 'ok',
    configured: hasApiKey,
    model: 'anthropic/claude-3.5-sonnet',
    message: hasApiKey 
      ? 'OpenRouter AI assistant is configured and ready' 
      : 'OpenRouter API key not configured - using mock responses'
  });
}

import { NextRequest, NextResponse } from 'next/server'
import bcrypt from 'bcryptjs'
import { PrismaClient } from '@prisma/client'
import { cookies } from 'next/headers'
import jwt from 'jsonwebtoken'

const prisma = new PrismaClient()

const JWT_SECRET = process.env.JWT_SECRET || 'your-secret-key'

// Map old schema to new schema format
const mapCompanyData = (company: any) => ({
  id: company.id,
  name: company.company_name,
  email: company.email,
  phone: company.phone,
  plan: company.plan || 'free',
  quotesUsed: company.quotes_used || 0,
  quotesLimit: company.quote_limit || 5,
  quotesResetAt: company.quotes_reset_at,
  quotesGenerated: company.quotes_generated || 0,
  settings: {
    chargeRates: {
      walls: parseFloat(company.default_walls_rate || '3.50'),
      ceilings: parseFloat(company.default_ceilings_rate || '4.00'),
      baseboards: 2.50,
      crownMolding: 5.00,
      doorsWithJams: 125.00,
      windows: 75.00,
      exteriorWalls: 4.50,
      fasciaBoards: 6.00,
      soffits: 5.00,
      exteriorDoors: 150.00,
      exteriorWindows: 100.00,
    },
    overheadPercent: 15,
    profitMargin: 30,
    taxRate: parseFloat(company.tax_rate || '8.25'),
    defaultTerms: 'Payment due within 30 days.'
  }
})

export async function POST(request: NextRequest) {
  try {
    const { email, password } = await request.json()

    if (!email || !password) {
      return NextResponse.json(
        { error: 'Email and password are required' },
        { status: 400 }
      )
    }

    // Query the old schema structure
    const result = await prisma.$queryRaw`
      SELECT 
        u.id as user_id,
        u.email,
        u.name,
        u.role,
        u.password_hash,
        c.*
      FROM company_users u
      JOIN companies c ON u.company_id = c.id
      WHERE u.email = ${email}
      LIMIT 1
    `

    const user = Array.isArray(result) && result.length > 0 ? result[0] : null

    if (!user) {
      return NextResponse.json(
        { error: 'Invalid credentials' },
        { status: 401 }
      )
    }

    // Verify password
    const isValid = await bcrypt.compare(password, user.password_hash)
    if (!isValid) {
      return NextResponse.json(
        { error: 'Invalid credentials' },
        { status: 401 }
      )
    }

    // Update last login
    await prisma.$executeRaw`
      UPDATE company_users 
      SET last_login_at = NOW() 
      WHERE id = ${user.user_id}
    `

    // Create JWT token
    const token = jwt.sign(
      {
        userId: user.user_id,
        companyId: user.id, // company id
        email: user.email,
        role: user.role,
      },
      JWT_SECRET,
      { expiresIn: '7d' }
    )

    // Set cookie
    cookies().set('auth-token', token, {
      httpOnly: true,
      secure: process.env.NODE_ENV === 'production',
      sameSite: 'lax',
      maxAge: 60 * 60 * 24 * 7, // 7 days
      path: '/',
    })

    // Map the response to match expected format
    return NextResponse.json({
      user: {
        id: user.user_id,
        email: user.email,
        name: user.name,
        role: user.role,
        company: mapCompanyData(user),
      },
    })
  } catch (error) {
    console.error('Sign in error:', error)
    return NextResponse.json(
      { error: 'Internal server error' },
      { status: 500 }
    )
  } finally {
    await prisma.$disconnect()
  }
}

