'use client';

import { useState, useEffect, Suspense } from 'react';
import { useRouter, useSearchParams } from 'next/navigation';
import { ChatInterface } from '@/components/chat/chat-interface';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Progress } from '@/components/ui/progress';
import { ArrowLeft, MessageSquare, List, AlertCircle, Sparkles } from 'lucide-react';
import Link from 'next/link';
import { getCompanyFromLocalStorage } from '@/lib/auth/simple-auth';

function CreateQuoteContent() {
  const searchParams = useSearchParams();
  const isDemo = searchParams.get('demo') === 'true';
  const [companyData, setCompanyData] = useState<any>(null);
  const [quotaInfo, setQuotaInfo] = useState<{
    used: number;
    limit: number;
    remaining: number;
    isUnlimited: boolean;
  } | null>(null);
  const router = useRouter();

  useEffect(() => {
    const data = getCompanyFromLocalStorage();
    if (!data) {
      router.push('/access-code');
      return;
    }
    setCompanyData(data);
    fetchQuotaInfo(data);
  }, [router]);

  const fetchQuotaInfo = async (company: any) => {
    try {
      const response = await fetch('/api/companies/usage', {
        headers: {
          'x-company-data': JSON.stringify({ 
            id: company.id,
            access_code: company.accessCode 
          })
        }
      });

      if (response.ok) {
        const data = await response.json();
        setQuotaInfo({
          used: data.currentMonth.quotesCreated,
          limit: data.currentMonth.limit,
          remaining: data.currentMonth.quotesRemaining,
          isUnlimited: data.currentMonth.limit === -1
        });
      }
    } catch (error) {
      console.error('Error fetching quota:', error);
    }
  };

  if (!companyData) {
    return (
      <div className="flex h-screen items-center justify-center">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-gray-900"></div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-900 via-slate-900 to-gray-900 flex flex-col">
      {/* Background Elements */}
      <div className="fixed inset-0 overflow-hidden pointer-events-none">
        <div className="absolute top-1/4 left-0 w-64 h-64 lg:w-96 lg:h-96 bg-blue-500 rounded-full opacity-10 blur-3xl"></div>
        <div className="absolute bottom-1/4 right-0 w-64 h-64 lg:w-96 lg:h-96 bg-purple-500 rounded-full opacity-10 blur-3xl"></div>
      </div>

      {/* Header */}
      <header className="glass-card rounded-none border-b border-white/10 relative z-20">
        <div className="container mx-auto px-4 py-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-4">
              <Link href="/dashboard">
                <Button variant="ghost" size="icon" className="text-white hover:bg-white/10">
                  <ArrowLeft className="h-5 w-5" />
                </Button>
              </Link>
              <h1 className="text-xl font-semibold text-white">Create Professional Quote</h1>
            </div>
            <div className="flex items-center gap-2">
              <span className="text-sm text-gray-300">
                ⚡ 2-minute quotes with AI
              </span>
            </div>
          </div>
        </div>
      </header>

      {/* Quota Display */}
      {quotaInfo && !quotaInfo.isUnlimited && (
        <div className="container mx-auto px-4 py-4 relative z-20">
          <Card className="glass-card border-white/10">
            <CardContent className="p-4">
              <div className="flex items-center justify-between">
                <div className="flex items-center gap-4">
                  <div>
                    <p className="text-sm text-gray-400">Monthly Quote Usage</p>
                    <p className="text-lg font-medium text-white">
                      {quotaInfo.used} / {quotaInfo.limit} quotes used
                    </p>
                  </div>
                  <Progress 
                    value={(quotaInfo.used / quotaInfo.limit) * 100} 
                    className="w-32 h-2 bg-white/10 [&>div]:bg-gradient-to-r [&>div]:from-blue-400 [&>div]:to-purple-400"
                  />
                </div>

                {quotaInfo.remaining <= 2 && quotaInfo.remaining > 0 && (
                  <Badge className="bg-amber-500/20 text-amber-400 border-amber-500/50">
                    <AlertCircle className="h-3 w-3 mr-1" />
                    {quotaInfo.remaining} quotes left
                  </Badge>
                )}

                {quotaInfo.remaining === 0 && (
                  <Link href="/dashboard/settings/billing">
                    <Button 
                      size="sm" 
                      className="bg-gradient-to-r from-blue-500 to-purple-500 hover:from-blue-600 hover:to-purple-600"
                    >
                      <Sparkles className="h-4 w-4 mr-2" />
                      Upgrade to Pro
                    </Button>
                  </Link>
                )}
              </div>
            </CardContent>
          </Card>
        </div>
      )}

      {/* Limit Reached Alert */}
      {quotaInfo && quotaInfo.remaining === 0 && (
        <div className="container mx-auto px-4 pb-4 relative z-20">
          <Card className="glass-card border-red-500/50 bg-red-500/10">
            <CardContent className="p-4">
              <div className="flex items-start gap-3">
                <AlertCircle className="h-5 w-5 text-red-400 mt-0.5" />
                <div className="flex-1">
                  <h3 className="font-medium text-white mb-1">Monthly Quote Limit Reached</h3>
                  <p className="text-sm text-gray-300 mb-3">
                    You've used all {quotaInfo.limit} quotes in your free plan this month. 
                    Upgrade to Pro for unlimited quotes and advanced features.
                  </p>
                  <Link href="/dashboard/settings/billing">
                    <Button size="sm" variant="outline" className="border-white/20 text-white">
                      View Upgrade Options
                    </Button>
                  </Link>
                </div>
              </div>
            </CardContent>
          </Card>
        </div>
      )}

      {/* Main content */}
      <main className="container mx-auto px-4 py-4 relative z-10 flex-1">
        <div className="mx-auto max-w-4xl h-full">
          <div className="glass-card h-full flex flex-col">
            <div className="p-6 border-b border-white/10">
              <h2 className="text-2xl font-bold text-white">
                Quick Quote Builder
              </h2>
              <p className="text-sm text-gray-300 mt-1">
                Just describe the project - get a professional quote in under 2 minutes
              </p>
            </div>
            <div className="flex-1 overflow-hidden">
              {quotaInfo && quotaInfo.remaining === 0 ? (
                <div className="flex items-center justify-center h-full">
                  <div className="text-center p-8">
                    <div className="w-16 h-16 bg-red-500/20 rounded-full flex items-center justify-center mx-auto mb-4">
                      <AlertCircle className="h-8 w-8 text-red-400" />
                    </div>
                    <h3 className="text-xl font-semibold text-white mb-2">Quote Limit Reached</h3>
                    <p className="text-gray-400 mb-6 max-w-md">
                      You've reached your monthly limit of {quotaInfo.limit} quotes. 
                      Upgrade to Pro for unlimited quotes and continue growing your business.
                    </p>
                    <Link href="/dashboard/settings/billing">
                      <Button className="bg-gradient-to-r from-blue-500 to-purple-500 hover:from-blue-600 hover:to-purple-600">
                        <Sparkles className="h-4 w-4 mr-2" />
                        Upgrade to Pro
                      </Button>
                    </Link>
                  </div>
                </div>
              ) : (
                <ChatInterface
                  companyId={companyData.id}
                  isDemo={isDemo}
                  onQuoteCreated={(quoteId) => {
                    router.push(`/dashboard/quotes/${quoteId}`);
                  }}
                />
              )}
            </div>
          </div>
        </div>
      </main>
    </div>
  );
}

export default function CreateQuotePage() {
  return (
    <Suspense fallback={
      <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-gray-900 via-gray-800 to-gray-900">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-white mx-auto mb-4"></div>
          <p className="text-gray-400">Loading quote builder...</p>
        </div>
      </div>
    }>
      <CreateQuoteContent />
    </Suspense>
  );
}

'use client'

import { useState, useEffect } from 'react'
import { useRouter, useParams } from 'next/navigation'
import { notFound } from 'next/navigation'
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card'
import { Button } from '@/components/ui/button'
import { Badge } from '@/components/ui/badge'
import { ArrowLeft, Phone, Mail, MapPin, Calendar, DollarSign, FileText, TrendingUp } from 'lucide-react'
import Link from 'next/link'
import { cn } from '@/lib/utils'
import { getCompanyFromLocalStorage } from '@/lib/auth/simple-auth'

interface Quote {
  id: number
  quote_number: string
  status: string
  total_amount: number
  created_at: string
}

interface Customer {
  id: number
  name: string
  email?: string
  phone?: string
  address?: string
  created_at: string
  quotes: Quote[]
}

export default function CustomerDetailPage() {
  const router = useRouter()
  const params = useParams()
  const [customer, setCustomer] = useState<Customer | null>(null)
  const [isLoading, setIsLoading] = useState(true)
  const [stats, setStats] = useState({
    totalQuotes: 0,
    acceptedQuotes: 0,
    totalRevenue: 0,
    acceptanceRate: 0,
    averageQuoteValue: 0
  })

  useEffect(() => {
    const company = getCompanyFromLocalStorage()
    if (!company) {
      router.push('/access-code')
      return
    }
    fetchCustomer(company)
  }, [router, params.id])

  const fetchCustomer = async (company: any) => {
    try {
      const response = await fetch(`/api/customers/${params.id}`, {
        headers: {
          'x-company-data': JSON.stringify({
            id: company.id,
            access_code: company.accessCode
          })
        }
      })

      if (!response.ok) {
        notFound()
        return
      }

      const data = await response.json()
      setCustomer(data)

      // Calculate stats
      const quotes = data.quotes || []
      const accepted = quotes.filter((q: Quote) => q.status === 'accepted')
      const totalRevenue = accepted.reduce((sum: number, q: Quote) => sum + q.total_amount, 0)

      setStats({
        totalQuotes: quotes.length,
        acceptedQuotes: accepted.length,
        totalRevenue,
        acceptanceRate: quotes.length > 0 ? Math.round((accepted.length / quotes.length) * 100) : 0,
        averageQuoteValue: accepted.length > 0 ? Math.round(totalRevenue / accepted.length) : 0
      })
    } catch (error) {
      console.error('Error fetching customer:', error)
      notFound()
    } finally {
      setIsLoading(false)
    }
  }

  const getStatusBadge = (status: string) => {
    const statusConfig = {
      draft: { label: 'Draft', className: 'bg-gray-500/20 text-gray-400 border-gray-500/50' },
      sent: { label: 'Sent', className: 'bg-blue-500/20 text-blue-400 border-blue-500/50' },
      viewed: { label: 'Viewed', className: 'bg-purple-500/20 text-purple-400 border-purple-500/50' },
      accepted: { label: 'Accepted', className: 'bg-emerald-500/20 text-emerald-400 border-emerald-500/50' },
      rejected: { label: 'Rejected', className: 'bg-red-500/20 text-red-400 border-red-500/50' }
    }

    const config = statusConfig[status as keyof typeof statusConfig] || statusConfig.draft

    return (
      <Badge className={cn('border', config.className)}>
        {config.label}
      </Badge>
    )
  }

  if (isLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-white mx-auto mb-4"></div>
          <p className="text-gray-400">Loading customer details...</p>
        </div>
      </div>
    )
  }

  if (!customer) {
    notFound()
  }

  return (
    <div className="space-y-8">
      {/* Header */}
      <div className="flex items-center gap-4">
        <Link href="/dashboard/customers">
          <Button variant="ghost" size="icon" className="text-white hover:bg-white/10">
            <ArrowLeft className="h-4 w-4" />
          </Button>
        </Link>
        <div>
          <h1 className="text-3xl font-bold text-gradient-modern">{customer.name}</h1>
          <p className="text-medium-contrast">Customer Details</p>
        </div>
      </div>

      {/* Contact Info */}
      <Card className="glass-card">
        <CardHeader>
          <CardTitle className="text-white">Contact Information</CardTitle>
        </CardHeader>
        <CardContent className="space-y-4">
          <div className="grid gap-4 md:grid-cols-3">
            {customer.email && (
              <div className="flex items-center gap-2 text-gray-300">
                <Mail className="h-4 w-4 text-gray-400" />
                <a href={`mailto:${customer.email}`} className="hover:text-blue-400 transition-colors">
                  {customer.email}
                </a>
              </div>
            )}
            {customer.phone && (
              <div className="flex items-center gap-2 text-gray-300">
                <Phone className="h-4 w-4 text-gray-400" />
                <a href={`tel:${customer.phone}`} className="hover:text-blue-400 transition-colors">
                  {customer.phone}
                </a>
              </div>
            )}
            {customer.address && (
              <div className="flex items-center gap-2 text-gray-300">
                <MapPin className="h-4 w-4 text-gray-400" />
                <span>{customer.address}</span>
              </div>
            )}
          </div>
          <div className="flex items-center gap-2 text-gray-400 text-sm">
            <Calendar className="h-4 w-4" />
            <span>Customer since {new Date(customer.created_at).toLocaleDateString()}</span>
          </div>
        </CardContent>
      </Card>

      {/* Stats */}
      <div className="grid gap-4 md:grid-cols-4">
        <Card className="glass-card">
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium text-gray-300">Total Quotes</CardTitle>
            <FileText className="h-4 w-4 text-blue-400" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-white">{stats.totalQuotes}</div>
          </CardContent>
        </Card>

        <Card className="glass-card">
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium text-gray-300">Acceptance Rate</CardTitle>
            <TrendingUp className="h-4 w-4 text-emerald-400" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-white">{stats.acceptanceRate}%</div>
          </CardContent>
        </Card>

        <Card className="glass-card">
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium text-gray-300">Total Revenue</CardTitle>
            <DollarSign className="h-4 w-4 text-emerald-400" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-white">
              ${stats.totalRevenue.toLocaleString()}
            </div>
          </CardContent>
        </Card>

        <Card className="glass-card">
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium text-gray-300">Avg Quote Value</CardTitle>
            <DollarSign className="h-4 w-4 text-purple-400" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-white">
              ${stats.averageQuoteValue.toLocaleString()}
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Quote History */}
      <Card className="glass-card">
        <CardHeader>
          <CardTitle className="text-white">Quote History</CardTitle>
          <CardDescription className="text-gray-400">
            All quotes for this customer
          </CardDescription>
        </CardHeader>
        <CardContent>
          {customer.quotes.length === 0 ? (
            <p className="text-center py-8 text-gray-400">No quotes yet for this customer</p>
          ) : (
            <div className="space-y-4">
              {customer.quotes.map((quote) => (
                <Link
                  key={quote.id}
                  href={`/dashboard/quotes/${quote.id}`}
                  className="block p-4 border border-white/10 rounded-lg hover:bg-white/5 transition-all group"
                >
                  <div className="flex items-center justify-between">
                    <div className="space-y-1">
                      <div className="flex items-center gap-3">
                        <h3 className="font-medium text-white group-hover:text-blue-400 transition-colors">
                          Quote #{quote.quote_number}
                        </h3>
                        {getStatusBadge(quote.status)}
                      </div>
                      <p className="text-xs text-gray-500">
                        {new Date(quote.created_at).toLocaleDateString()}
                      </p>
                    </div>

                    <div className="text-right">
                      <p className="text-xl font-bold text-white">
                        ${quote.total_amount.toLocaleString()}
                      </p>
                    </div>
                  </div>
                </Link>
              ))}
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  )
}

'use client'

import { useState, useEffect } from 'react'
import { useRouter } from 'next/navigation'
import Link from 'next/link'
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card'
import { Button } from '@/components/ui/button'
import { Phone, Mail, MapPin, FileText, Users, DollarSign, TrendingUp } from 'lucide-react'
import { getCompanyFromLocalStorage } from '@/lib/auth/simple-auth'

interface Customer {
  id: number
  name: string
  email?: string
  phone?: string
  address?: string
  totalQuotes: number
  acceptedQuotes: number
  totalRevenue: number
  lastQuoteDate: string
  created_at: string
}

export default function CustomersPage() {
  const router = useRouter()
  const [customers, setCustomers] = useState<Customer[]>([])
  const [isLoading, setIsLoading] = useState(true)
  const [companyData, setCompanyData] = useState<any>(null)

  useEffect(() => {
    const company = getCompanyFromLocalStorage()
    if (!company) {
      router.push('/access-code')
      return
    }
    setCompanyData(company)
    fetchCustomers(company)
  }, [router])

  const fetchCustomers = async (company: any) => {
    try {
      const response = await fetch('/api/customers', {
        headers: {
          'x-company-data': JSON.stringify({
            id: company.id,
            access_code: company.accessCode
          })
        }
      })

      if (response.ok) {
        const data = await response.json()
        setCustomers(data.customers || [])
      }
    } catch (error) {
      console.error('Error fetching customers:', error)
    } finally {
      setIsLoading(false)
    }
  }

  if (isLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-white mx-auto mb-4"></div>
          <p className="text-gray-400">Loading customers...</p>
        </div>
      </div>
    )
  }

  const totalQuotes = customers.reduce((sum, c) => sum + c.totalQuotes, 0)
  const totalRevenue = customers.reduce((sum, c) => sum + c.totalRevenue, 0)

  return (
    <div className="space-y-8">
      <div>
        <h1 className="text-3xl font-bold text-gradient-modern">Customers</h1>
        <p className="text-medium-contrast">
          Manage your customer relationships and quote history
        </p>
      </div>

