          )}
        </CardContent>
      </Card>
    </div>
  )
}

'use client'

import Link from 'next/link'
import { usePathname, useRouter } from 'next/navigation'
import { LayoutGrid, FileText, Users, Settings, BarChart3, Calculator, LogOut, Sparkles, CreditCard, Zap } from 'lucide-react'
import { useEffect, useState } from 'react'
import { OnboardingModal } from '@/components/onboarding-modal'

interface CompanyData {
  id: number
  name: string
  email: string
  accessCode: string
  onboarding_completed?: boolean
}

export default function DashboardLayout({
  children,
}: {
  children: React.ReactNode
}) {
  const pathname = usePathname()
  const router = useRouter()
  const [company, setCompany] = useState<CompanyData | null>(null)
  const [loading, setLoading] = useState(true)
  const [showOnboarding, setShowOnboarding] = useState(false)

  useEffect(() => {
    const storedData = localStorage.getItem('paintquote_company')
    if (!storedData) {
      router.push('/access-code')
      return
    }

    try {
      const data = JSON.parse(storedData)
      setCompany({
        id: data.id,
        name: data.name,
        email: data.email,
        accessCode: data.accessCode,
        onboarding_completed: data.onboarding_completed
      })

      // Check if onboarding is needed
      if (!data.onboarding_completed && !data.skipOnboarding && pathname !== '/onboarding' && pathname !== '/onboarding/chat') {
        setShowOnboarding(true)
      }
    } catch (error) {
      console.error('Error parsing company data:', error)
      router.push('/access-code')
    } finally {
      setLoading(false)
    }
  }, [router])

  const handleSignOut = () => {
    localStorage.removeItem('paintquote_company')
    router.push('/access-code')
  }

  const navItems = [
    { href: '/dashboard', label: 'Overview', icon: LayoutGrid, color: 'from-blue-400 to-cyan-400' },
    { href: '/create-quote', label: 'Quick Quote', icon: Zap, color: 'from-purple-400 to-pink-400' },
    { href: '/dashboard/quotes', label: 'Quote Pipeline', icon: FileText, color: 'from-emerald-400 to-green-400' },
    { href: '/dashboard/customers', label: 'Customers', icon: Users, color: 'from-amber-400 to-orange-400' },
    { href: '/billing', label: 'Billing', icon: CreditCard, color: 'from-rose-400 to-pink-400' },
    { href: '/dashboard/settings', label: 'Settings', icon: Settings, color: 'from-slate-400 to-gray-400' },
  ]

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen bg-gradient-to-br from-gray-900 to-slate-900">
        <div className="text-center glass-card p-8">
          <div className="relative w-16 h-16 mx-auto mb-4">
            <div className="absolute inset-0 bg-gradient-to-r from-blue-400 to-purple-400 rounded-full blur-lg opacity-75 animate-pulse"></div>
            <div className="relative w-16 h-16 bg-gradient-to-br from-blue-400 to-purple-400 rounded-full animate-spin"></div>
          </div>
          <p className="text-white font-medium">Loading dashboard...</p>
        </div>
      </div>
    )
  }

  if (!company) {
    return null
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-900 via-slate-900 to-gray-900">
      {/* Background Elements */}
      <div className="fixed inset-0 overflow-hidden pointer-events-none">
        <div className="absolute top-1/4 left-0 w-64 h-64 lg:w-96 lg:h-96 bg-blue-500 rounded-full opacity-10 blur-3xl"></div>
        <div className="absolute bottom-1/4 right-0 w-64 h-64 lg:w-96 lg:h-96 bg-purple-500 rounded-full opacity-10 blur-3xl"></div>
      </div>

      {/* Layout Container */}
      <div className="relative z-10 min-h-screen flex">
        {/* Sidebar */}
        <div className="hidden lg:flex top-0 left-0 h-full glass-card rounded-none border-r border-white/10 z-30 flex-col" style={{ position: 'fixed', width: '256px' }}>
            {/* Logo */}
            <Link href="/dashboard" className="flex items-center gap-3 p-6 border-b border-white/10 group">
              <div className="relative">
                <div className="absolute inset-0 bg-gradient-to-r from-slate-400 to-slate-600 rounded-xl blur-md opacity-50 group-hover:opacity-75 transition-opacity"></div>
                <div className="relative w-10 h-10 bg-gradient-to-br from-slate-400 to-slate-600 rounded-xl flex items-center justify-center">
                  <span className="text-white font-black text-xl">P</span>
                </div>
              </div>
              <div>
                <div className="font-bold text-white">PaintQuote Pro</div>
                <div className="text-xs text-gray-400">{company.name}</div>
              </div>
            </Link>

            {/* Navigation */}
            <nav className="flex-1 p-4 space-y-2 overflow-y-auto">
              {navItems.map((item) => {
                const Icon = item.icon
                const isActive = pathname === item.href

                return (
                  <Link
                    key={item.href}
                    href={item.href}
                    className={`
                      relative flex items-center gap-3 px-4 py-3 rounded-xl transition-all duration-300 group
                      ${isActive 
                        ? 'bg-white/10 text-white' 
                        : 'text-gray-400 hover:text-white hover:bg-white/5'
                      }
                    `}
                  >
                    {isActive && (
                      <div className={`absolute inset-0 bg-gradient-to-r ${item.color} opacity-20 rounded-xl blur-md`}></div>
                    )}
                    <div className={`relative flex items-center justify-center w-9 h-9 rounded-lg bg-gradient-to-br ${item.color} ${isActive ? '' : 'opacity-60 group-hover:opacity-100'} transition-opacity`}>
                      <Icon className="h-5 w-5 text-white" />
                    </div>
                    <span className="relative font-medium">{item.label}</span>
                    {isActive && (
                      <div className="absolute right-4 w-1.5 h-1.5 bg-white rounded-full"></div>
                    )}
                  </Link>
                )
              })}
            </nav>

            {/* Bottom Section */}
            <div className="p-4 border-t border-white/10">
              <div className="glass-card p-4 mb-4">
                <div className="flex items-center gap-2 mb-2">
                  <Sparkles className="h-4 w-4 text-purple-400" />
                  <span className="text-sm font-medium text-white">Pro Tip</span>
                </div>
                <p className="text-xs text-gray-400">Create quotes 95% faster with our AI assistant</p>
              </div>

              <button
                onClick={handleSignOut}
                className="flex items-center gap-3 w-full px-4 py-3 text-gray-400 hover:text-white hover:bg-white/5 rounded-xl transition-all duration-300"
              >
                <LogOut className="h-5 w-5" />
                <span className="font-medium">Sign Out</span>
              </button>
            </div>
        </div>

        {/* Main Content */}
        <main className="w-full lg:pl-64 min-h-screen flex flex-col">
          {/* Top Bar */}
          <div className="h-20 glass-card rounded-none border-b border-white/10 px-8 flex items-center">
            <div className="flex items-center justify-between w-full">
              <h2 className="text-2xl font-bold text-white">
                {navItems.find(item => item.href === pathname)?.label || 'Dashboard'}
              </h2>
              <div className="flex items-center gap-4">
                <span className="text-sm text-gray-400 hidden md:block">⚡ {company.email}</span>
                <Link href="/create-quote" className="group relative">
                  <div className="absolute -inset-1 bg-gradient-to-r from-blue-400 to-purple-400 rounded-lg blur-sm opacity-50 group-hover:opacity-75 transition-opacity"></div>
                  <div className="relative btn-primary-modern inline-flex items-center gap-2">
                    <Sparkles className="h-4 w-4" />
                    New Quote
                  </div>
                </Link>
              </div>
            </div>
          </div>

          {/* Page Content */}
          <div className="flex-1 p-4 lg:p-8 pb-20 lg:pb-8">
            {children}
          </div>
        </main>
      </div>

      {/* Mobile Navigation */}
      <nav className="lg:hidden fixed bottom-0 left-0 right-0 glass-card rounded-t-2xl border-t border-white/10">
        <div className="grid grid-cols-4 gap-1 p-2">
          {navItems.slice(0, 4).map((item) => {
            const Icon = item.icon
            const isActive = pathname === item.href

            return (
              <Link
                key={item.href}
                href={item.href}
                className={`
                  flex flex-col items-center gap-1 py-2 px-3 rounded-lg transition-all
                  ${isActive 
                    ? 'bg-white/10 text-white' 
                    : 'text-gray-400 hover:text-white hover:bg-white/5'
                  }
                `}
              >
                <div className={`relative flex items-center justify-center w-8 h-8 rounded-lg bg-gradient-to-br ${item.color} ${isActive ? '' : 'opacity-60'} transition-opacity`}>
                  <Icon className="h-4 w-4 text-white" />
                </div>
                <span className="text-xs font-medium">{item.label}</span>
              </Link>
            )
          })}
        </div>
      </nav>

      {/* Onboarding Modal */}
      <OnboardingModal 
        isOpen={showOnboarding} 
        onClose={() => setShowOnboarding(false)}
        companyData={company}
      />
    </div>
  )
}

'use client'

import { useEffect, useState } from 'react'
import { useRouter } from 'next/navigation'
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card'
import { Button } from '@/components/ui/button'
import { Input } from '@/components/ui/input'
import { Label } from '@/components/ui/label'
import { ArrowLeft, Save, Send, Mail, Copy, Edit3, Check, X, ExternalLink } from 'lucide-react'
import Link from 'next/link'
import { toast } from '@/components/ui/use-toast'

interface Quote {
  id: number
  quote_id: string
  customer_name: string
  customer_email?: string
  customer_phone?: string
  address?: string
  project_type: string
  rooms?: string
  total_revenue: number
  final_price: number
  base_cost: number
  total_materials: number
  projected_labor: number
  markup_percentage: number
  created_at: string
  status: string
  walls_sqft?: number
  paint_quality?: string
  timeline?: string
  special_requests?: string
  conversation_summary?: string
  paint_cost?: number
  sundries_cost?: number
  tax_rate?: number
  tax_amount?: number
  subtotal?: number
}

export default function QuotePreviewPage({ params }: { params: { id: string } }) {
  const [quote, setQuote] = useState<Quote | null>(null)
  const [loading, setLoading] = useState(true)
  const [editing, setEditing] = useState(false)
  const [editedQuote, setEditedQuote] = useState<Quote | null>(null)
  const [saving, setSaving] = useState(false)
  const router = useRouter()

  useEffect(() => {
    fetchQuote()
  }, [params.id])

  const fetchQuote = async () => {
    try {
      const response = await fetch(`/api/quotes/${params.id}`)
      if (!response.ok) {
        throw new Error('Failed to fetch quote')
      }
      const data = await response.json()
      setQuote(data.quote)
      setEditedQuote(data.quote)
    } catch (error) {
      console.error('Error fetching quote:', error)
      toast({
        title: 'Error',
        description: 'Failed to load quote details',
        variant: 'destructive'
      })
    } finally {
      setLoading(false)
    }
  }

  const handleEdit = () => {
    setEditing(true)
  }

  const handleCancel = () => {
    setEditing(false)
    setEditedQuote(quote)
  }

  const handleSave = async () => {
    if (!editedQuote) return

    setSaving(true)
    try {
      // Recalculate totals
      const materials = parseFloat(editedQuote.total_materials?.toString() || '0')
      const labor = parseFloat(editedQuote.projected_labor?.toString() || '0')
      const subtotal = materials + labor
      const markupAmount = subtotal * (editedQuote.markup_percentage / 100)
      const beforeTax = subtotal + markupAmount
      const taxAmount = beforeTax * (editedQuote.tax_rate || 0) / 100
      const finalPrice = beforeTax + taxAmount

      const updatedQuote = {
        ...editedQuote,
        subtotal,
        tax_amount: taxAmount,
        final_price: finalPrice,
        total_revenue: finalPrice
      }

      const response = await fetch(`/api/quotes/${params.id}`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(updatedQuote)
      })

      if (!response.ok) {
        throw new Error('Failed to update quote')
      }

      setQuote(updatedQuote)
      setEditedQuote(updatedQuote)
      setEditing(false)

      toast({
        title: 'Success',
        description: 'Quote updated successfully'
      })
    } catch (error) {
      console.error('Error updating quote:', error)
      toast({
        title: 'Error',
        description: 'Failed to update quote',
        variant: 'destructive'
      })
    } finally {
      setSaving(false)
    }
  }

  const handleSendEmail = async () => {
    if (!quote) return

    // Track that the quote was sent
    try {
      await fetch(`/api/quotes/${params.id}/track`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ event: 'sent' })
      })
    } catch (error) {
      console.error('Error tracking quote sent:', error)
    }

    const subject = `Painting Quote #${quote.quote_id}`
    const body = formatEmailBody(quote)

    window.location.href = `mailto:${quote.customer_email || ''}?subject=${encodeURIComponent(subject)}&body=${encodeURIComponent(body)}`
  }

  const copyPublicLink = async () => {
    const publicUrl = `${window.location.origin}/quote/${params.id}`
    try {
      await navigator.clipboard.writeText(publicUrl)
      toast({
        title: 'Link Copied!',
        description: 'The quote link has been copied to your clipboard.'
      })
    } catch (error) {
      toast({
        title: 'Error',
        description: 'Failed to copy link',
        variant: 'destructive'
      })
    }
  }

  const formatEmailBody = (quote: Quote) => {
    const publicUrl = `${window.location.origin}/quote/${params.id}`
    return `Dear ${quote.customer_name},

Thank you for considering us for your painting project. Please find your quote details below:

QUOTE #${quote.quote_id}
Date: ${new Date(quote.created_at).toLocaleDateString()}

PROJECT DETAILS:
- Type: ${quote.project_type} painting
- Total area: ${quote.walls_sqft || 0} sq ft
- Paint quality: ${quote.paint_quality || 'Standard'}
- Timeline: ${quote.timeline || 'Standard'}
${quote.special_requests ? `- Special requests: ${quote.special_requests}` : ''}

COST BREAKDOWN:
- Materials: $${quote.total_materials?.toFixed(2) || '0.00'}
- Labor: $${quote.projected_labor?.toFixed(2) || '0.00'}
- Subtotal: $${quote.subtotal?.toFixed(2) || '0.00'}
- Markup (${quote.markup_percentage}%): $${((quote.subtotal || 0) * quote.markup_percentage / 100).toFixed(2)}
${quote.tax_rate ? `- Tax (${quote.tax_rate}%): $${quote.tax_amount?.toFixed(2) || '0.00'}` : ''}

TOTAL: $${quote.final_price?.toFixed(2) || '0.00'}

This quote is valid for 30 days from the date above.

View your quote online: ${publicUrl}

Please let us know if you have any questions or would like to proceed with the project.

Best regards,
Your Painting Company`
  }

  if (loading) {
    return (
      <div className="flex h-[50vh] items-center justify-center">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-gray-900"></div>
      </div>
    )
  }

  if (!quote || !editedQuote) {
    return (
      <div className="mx-auto max-w-4xl">
        <Card>
          <CardContent className="p-8 text-center">
            <p>Quote not found</p>
            <Link href="/dashboard/quotes">
              <Button className="mt-4">Back to Quotes</Button>
            </Link>
          </CardContent>
        </Card>
      </div>
    )
  }

  return (
    <div className="mx-auto max-w-4xl space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div className="flex items-center gap-4">
          <Link href="/dashboard/quotes">
            <Button variant="ghost" size="icon">
              <ArrowLeft className="h-4 w-4" />
            </Button>
          </Link>
          <div>
            <h1 className="text-2xl font-bold">Quote Preview</h1>
            <p className="text-sm text-muted-foreground">
              Review and edit before sending to customer
            </p>
          </div>
        </div>
        <div className="flex gap-2">
          {editing ? (
            <>
              <Button variant="outline" onClick={handleCancel} disabled={saving}>
                <X className="h-4 w-4 mr-2" />
                Cancel
              </Button>
              <Button onClick={handleSave} disabled={saving}>
                <Save className="h-4 w-4 mr-2" />
                {saving ? 'Saving...' : 'Save Changes'}
              </Button>
            </>
          ) : (
            <>
              <Button variant="outline" onClick={handleEdit}>
                <Edit3 className="h-4 w-4 mr-2" />
                Edit
              </Button>
              <Button onClick={handleSendEmail} disabled={!quote.customer_email}>
                <Mail className="h-4 w-4 mr-2" />
                Send Email
              </Button>
              <Button onClick={copyPublicLink} variant="outline">
                <Copy className="h-4 w-4 mr-2" />
                Copy Link
              </Button>
            </>
          )}
        </div>
      </div>

      {/* Quote Header */}
      <Card>
        <CardHeader>
          <CardTitle>Quote #{quote.quote_id}</CardTitle>
          <CardDescription>
            Created on {new Date(quote.created_at).toLocaleDateString()}
          </CardDescription>
        </CardHeader>
      </Card>

      {/* Customer Information */}
      <Card>
        <CardHeader>
          <CardTitle>Customer Information</CardTitle>
        </CardHeader>
        <CardContent className="grid gap-4 md:grid-cols-2">
          <div>
            <Label>Name</Label>
            {editing ? (
              <Input
                value={editedQuote.customer_name}
                onChange={(e) => setEditedQuote({...editedQuote, customer_name: e.target.value})}
              />
            ) : (
              <p className="mt-1">{quote.customer_name}</p>
            )}
          </div>
          <div>
            <Label>Email</Label>
            {editing ? (
              <Input
                type="email"
                value={editedQuote.customer_email || ''}
                onChange={(e) => setEditedQuote({...editedQuote, customer_email: e.target.value})}
              />
            ) : (
              <p className="mt-1">{quote.customer_email || 'Not provided'}</p>
            )}
          </div>
          <div>
            <Label>Phone</Label>
            {editing ? (
              <Input
                value={editedQuote.customer_phone || ''}
                onChange={(e) => setEditedQuote({...editedQuote, customer_phone: e.target.value})}
              />
            ) : (
              <p className="mt-1">{quote.customer_phone || 'Not provided'}</p>
            )}
          </div>
          <div>
            <Label>Address</Label>
            {editing ? (
              <Input
                value={editedQuote.address || ''}
                onChange={(e) => setEditedQuote({...editedQuote, address: e.target.value})}
              />
            ) : (
              <p className="mt-1">{quote.address || 'Not provided'}</p>
            )}
          </div>
        </CardContent>
      </Card>

      {/* Project Details */}
      <Card>
        <CardHeader>
          <CardTitle>Project Details</CardTitle>
        </CardHeader>
        <CardContent className="space-y-4">
          <div className="grid gap-4 md:grid-cols-2">
            <div>
              <Label>Wall Area (sq ft)</Label>
              {editing ? (
                <Input
                  type="number"
                  value={editedQuote.walls_sqft || 0}
                  onChange={(e) => setEditedQuote({...editedQuote, walls_sqft: parseInt(e.target.value) || 0})}
                />
              ) : (
                <p className="mt-1">{quote.walls_sqft || 0} sq ft</p>
              )}
            </div>
            <div>
              <Label>Paint Quality</Label>
              {editing ? (
                <Input
                  value={editedQuote.paint_quality || ''}
                  onChange={(e) => setEditedQuote({...editedQuote, paint_quality: e.target.value})}
                />
              ) : (
                <p className="mt-1">{quote.paint_quality || 'Standard'}</p>
              )}
            </div>
            <div>
              <Label>Timeline</Label>
              {editing ? (
                <Input
                  value={editedQuote.timeline || ''}
                  onChange={(e) => setEditedQuote({...editedQuote, timeline: e.target.value})}
                />
              ) : (
