            </div>
            <div className="flex gap-2">
              <Button variant="outline" onClick={() => router.push(`/dashboard/quotes/${params.id}`)}>
                View Final
              </Button>
              <Button onClick={handleSendEmail} disabled={!quote.customer_email}>
                <Send className="h-4 w-4 mr-2" />
