'use client'

import { useState, useEffect } from 'react'
import { useRouter } from 'next/navigation'
import Link from 'next/link'
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card'
import { Badge } from '@/components/ui/badge'
import { Button } from '@/components/ui/button'
import { FileText, DollarSign, Clock, CheckCircle, XCircle, Send, Plus } from 'lucide-react'
import { getCompanyFromLocalStorage } from '@/lib/auth/simple-auth'
import { cn } from '@/lib/utils'

interface Quote {
  id: number
  quote_number: string
  status: string
  total_amount: number
  created_at: string
  customer?: {
    name: string
    email?: string
    phone?: string
  }
}

export default function QuotesPage() {
  const router = useRouter()
  const [quotes, setQuotes] = useState<Quote[]>([])
  const [isLoading, setIsLoading] = useState(true)
  const [stats, setStats] = useState({
    totalQuotes: 0,
    acceptedQuotes: 0,
    totalValue: 0,
    acceptanceRate: 0
  })

  useEffect(() => {
    const company = getCompanyFromLocalStorage()
    if (!company) {
      router.push('/access-code')
      return
    }
    fetchQuotes(company)
  }, [router])

  const fetchQuotes = async (company: any) => {
    try {
      const response = await fetch('/api/quotes', {
        headers: {
          'x-company-data': JSON.stringify({
            id: company.id,
            access_code: company.accessCode
          })
        }
      })

      if (response.ok) {
        const data = await response.json()
        const quotesData = data.quotes || []
        setQuotes(quotesData)

        // Calculate stats
        const accepted = quotesData.filter((q: Quote) => q.status === 'accepted')
        const totalValue = accepted.reduce((sum: number, q: Quote) => sum + q.total_amount, 0)

        setStats({
          totalQuotes: quotesData.length,
          acceptedQuotes: accepted.length,
          totalValue,
          acceptanceRate: quotesData.length > 0 ? Math.round((accepted.length / quotesData.length) * 100) : 0
        })
      }
    } catch (error) {
      console.error('Error fetching quotes:', error)
    } finally {
      setIsLoading(false)
    }
  }

  const getStatusBadge = (status: string) => {
    const statusConfig = {
      draft: { label: 'Draft', className: 'bg-gray-500/20 text-gray-400 border-gray-500/50' },
      sent: { label: 'Sent', className: 'bg-blue-500/20 text-blue-400 border-blue-500/50' },
      viewed: { label: 'Viewed', className: 'bg-purple-500/20 text-purple-400 border-purple-500/50' },
      accepted: { label: 'Accepted', className: 'bg-emerald-500/20 text-emerald-400 border-emerald-500/50' },
      rejected: { label: 'Rejected', className: 'bg-red-500/20 text-red-400 border-red-500/50' }
    }

    const config = statusConfig[status as keyof typeof statusConfig] || statusConfig.draft

    return (
      <Badge className={cn('border', config.className)}>
        {config.label}
      </Badge>
    )
  }

  if (isLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-white mx-auto mb-4"></div>
          <p className="text-gray-400">Loading quotes...</p>
        </div>
      </div>
    )
  }

  return (
    <div className="space-y-8">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold text-gradient-modern">Quotes</h1>
          <p className="text-medium-contrast">
            Manage and track all your painting quotes
          </p>
        </div>
        <Link href="/create-quote">
          <Button className="btn-primary-modern">
            <Plus className="h-4 w-4 mr-2" />
            New Quote
          </Button>
        </Link>
      </div>

      {/* Stats Cards */}
      <div className="grid gap-4 md:grid-cols-4">
        <Card className="glass-card">
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium text-gray-300">Total Quotes</CardTitle>
            <FileText className="h-4 w-4 text-blue-400" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-white">{stats.totalQuotes}</div>
            <p className="text-xs text-gray-400 mt-1">All time</p>
          </CardContent>
        </Card>

        <Card className="glass-card">
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium text-gray-300">Accepted</CardTitle>
            <CheckCircle className="h-4 w-4 text-emerald-400" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-white">{stats.acceptedQuotes}</div>
            <p className="text-xs text-gray-400 mt-1">{stats.acceptanceRate}% acceptance rate</p>
          </CardContent>
        </Card>

        <Card className="glass-card">
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium text-gray-300">Total Value</CardTitle>
            <DollarSign className="h-4 w-4 text-emerald-400" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-white">
              ${stats.totalValue.toLocaleString()}
            </div>
            <p className="text-xs text-gray-400 mt-1">From accepted quotes</p>
          </CardContent>
        </Card>

        <Card className="glass-card">
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium text-gray-300">Average Quote</CardTitle>
            <Clock className="h-4 w-4 text-purple-400" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-white">
              ${stats.acceptedQuotes > 0 ? Math.round(stats.totalValue / stats.acceptedQuotes).toLocaleString() : '0'}
            </div>
            <p className="text-xs text-gray-400 mt-1">Per accepted quote</p>
          </CardContent>
        </Card>
      </div>

      {/* Quotes List */}
      <Card className="glass-card">
        <CardHeader>
          <CardTitle className="text-white">Recent Quotes</CardTitle>
        </CardHeader>
        <CardContent>
          {quotes.length === 0 ? (
            <div className="text-center py-12">
              <FileText className="h-12 w-12 text-gray-600 mx-auto mb-4" />
              <h3 className="text-lg font-semibold text-white mb-2">No quotes yet</h3>
              <p className="text-gray-400 mb-4">Create your first quote to get started</p>
              <Link href="/create-quote">
                <Button className="btn-primary-modern">
                  Create Your First Quote
                </Button>
              </Link>
            </div>
          ) : (
            <div className="space-y-4">
              {quotes.map((quote) => (
                <Link
                  key={quote.id}
                  href={`/dashboard/quotes/${quote.id}`}
                  className="block p-4 border border-white/10 rounded-lg hover:bg-white/5 transition-all group"
                >
                  <div className="flex items-center justify-between">
                    <div className="space-y-1">
                      <div className="flex items-center gap-3">
                        <h3 className="font-medium text-white group-hover:text-blue-400 transition-colors">
                          Quote #{quote.quote_number}
                        </h3>
                        {getStatusBadge(quote.status)}
                      </div>
                      {quote.customer && (
                        <p className="text-sm text-gray-400">
                          {quote.customer.name}
                          {quote.customer.email && ` • ${quote.customer.email}`}
                        </p>
                      )}
                      <p className="text-xs text-gray-500">
                        Created {new Date(quote.created_at).toLocaleDateString()}
                      </p>
                    </div>

                    <div className="text-right">
                      <p className="text-xl font-bold text-white">
                        ${quote.total_amount.toLocaleString()}
                      </p>
                    </div>
                  </div>
                </Link>
              ))}
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  )
}

'use client'

import { useState, useEffect } from 'react'
import { useRouter } from 'next/navigation'
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card'
import { Button } from '@/components/ui/button'
import { Badge } from '@/components/ui/badge'
import { Progress } from '@/components/ui/progress'
import { toast } from '@/components/ui/use-toast'
import { getCompanyFromLocalStorage } from '@/lib/auth/simple-auth'
import { SUBSCRIPTION_TIERS } from '@/lib/services/subscription'
import { redirectToStripePayment } from '@/lib/config/stripe-links'
import { 
  CreditCard, 
  Check, 
  X, 
  Sparkles, 
  TrendingUp,
  Clock,
  AlertCircle,
  ChevronRight
} from 'lucide-react'

interface UsageStats {
  currentMonth: {
    quotesCreated: number;
    quotesRemaining: number;
    limit: number;
  };
  lastMonth: {
    quotesCreated: number;
    quotesAccepted: number;
    conversionRate: number;
    avgResponseTime: number;
  };
}

export default function BillingPage() {
  const router = useRouter()
  const [company, setCompany] = useState<any>(null)
  const [loading, setLoading] = useState(true)
  const [usage, setUsage] = useState<UsageStats | null>(null)
  const [processingUpgrade, setProcessingUpgrade] = useState(false)

  useEffect(() => {
    const companyData = getCompanyFromLocalStorage()
    if (!companyData) {
      router.push('/access-code')
      return
    }
    setCompany(companyData)
    fetchUsageStats()
  }, [router])

  const fetchUsageStats = async () => {
    try {
      const response = await fetch('/api/companies/usage', {
        headers: {
          'x-company-data': JSON.stringify({ 
            id: company?.id,
            access_code: company?.accessCode 
          })
        }
      })

      if (response.ok) {
        const data = await response.json()
        setUsage(data)
      }
    } catch (error) {
      console.error('Error fetching usage:', error)
    } finally {
      setLoading(false)
    }
  }

  const handleUpgrade = async (tier: string) => {
    setProcessingUpgrade(true)
    try {
      toast({
        title: 'Redirecting to Stripe',
        description: 'You will be redirected to complete your purchase...'
      })

      // Redirect to Stripe payment link
      setTimeout(() => {
        if (tier === 'pro') {
          redirectToStripePayment('professional', 'monthly')
        } else if (tier === 'business') {
          redirectToStripePayment('business', 'monthly')
        }
      }, 1000)
    } catch (error) {
      toast({
        title: 'Error',
        description: 'Failed to redirect to payment. Please try again.',
        variant: 'destructive'
      })
      setProcessingUpgrade(false)
    }
  }

  const currentTier = company?.subscription_tier || 'free'
  const tierInfo = SUBSCRIPTION_TIERS[currentTier]

  if (loading) {
    return <div className="flex items-center justify-center min-h-[400px]">
      <div className="text-center">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-white mx-auto mb-4"></div>
        <p className="text-gray-400">Loading billing information...</p>
      </div>
    </div>
  }

  const usagePercentage = usage && usage.currentMonth.limit !== -1 
    ? (usage.currentMonth.quotesCreated / usage.currentMonth.limit) * 100 
    : 0

  return (
    <div className="space-y-8">
      {/* Current Plan */}
      <Card className="glass-card">
        <CardHeader>
          <CardTitle className="text-white">Current Plan</CardTitle>
          <CardDescription className="text-gray-400">
            Manage your subscription and billing
          </CardDescription>
        </CardHeader>
        <CardContent>
          <div className="flex items-center justify-between mb-6">
            <div>
              <div className="flex items-center gap-3">
                <h3 className="text-2xl font-bold text-white">{tierInfo.displayName}</h3>
                {currentTier === 'pro' && (
                  <Badge className="bg-gradient-to-r from-blue-500 to-purple-500 text-white border-0">
                    <Sparkles className="h-3 w-3 mr-1" />
                    Pro
                  </Badge>
                )}
              </div>
              <p className="text-gray-400 mt-1">
                {currentTier === 'free' 
                  ? 'Perfect for getting started'
                  : 'Unlimited quotes and advanced features'}
              </p>
            </div>
            <div className="text-right">
              <div className="text-3xl font-bold text-white">
                ${tierInfo.price}
                <span className="text-sm font-normal text-gray-400">/month</span>
              </div>
            </div>
          </div>

          {/* Usage Stats */}
          {usage && currentTier === 'free' && (
            <div className="mb-6">
              <div className="flex justify-between text-sm mb-2">
                <span className="text-gray-400">Monthly Quote Usage</span>
                <span className="text-white">
                  {usage.currentMonth.quotesCreated} / {usage.currentMonth.limit}
                </span>
              </div>
              <Progress value={usagePercentage} className="h-2 mb-2" />
              {usagePercentage >= 80 && (
                <div className="flex items-center gap-2 text-amber-400 text-sm">
                  <AlertCircle className="h-4 w-4" />
                  <span>You're approaching your monthly limit</span>
                </div>
              )}
            </div>
          )}

          {/* Performance Stats */}
          <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-6">
            <div className="glass-card p-4">
              <div className="text-2xl font-bold text-white">
                {usage?.lastMonth.quotesCreated || 0}
              </div>
              <p className="text-sm text-gray-400">Quotes Last Month</p>
            </div>
            <div className="glass-card p-4">
              <div className="text-2xl font-bold text-green-400">
                {usage?.lastMonth.conversionRate || 0}%
              </div>
              <p className="text-sm text-gray-400">Win Rate</p>
            </div>
            <div className="glass-card p-4">
              <div className="text-2xl font-bold text-blue-400">
                {usage?.lastMonth.avgResponseTime || 0}h
              </div>
              <p className="text-sm text-gray-400">Avg Response Time</p>
            </div>
            <div className="glass-card p-4">
              <div className="text-2xl font-bold text-purple-400">
                ${((usage?.lastMonth.quotesAccepted || 0) * 2800).toLocaleString()}
              </div>
              <p className="text-sm text-gray-400">Est. Revenue</p>
            </div>
          </div>

          {currentTier === 'free' && (
            <div className="bg-gradient-to-r from-blue-500/10 to-purple-500/10 rounded-lg p-4 border border-blue-500/20">
              <div className="flex items-start gap-3">
                <TrendingUp className="h-5 w-5 text-blue-400 mt-0.5" />
                <div>
                  <p className="text-white font-medium mb-1">
                    Unlock Your Full Potential
                  </p>
                  <p className="text-sm text-gray-300">
                    Based on your usage, upgrading to Pro could help you win {Math.round((usage?.lastMonth.quotesCreated || 20) * 0.15)} more jobs per month
                  </p>
                </div>
              </div>
            </div>
          )}
        </CardContent>
      </Card>

      {/* Pricing Comparison */}
      <div>
        <h2 className="text-2xl font-bold text-white mb-6">
          {currentTier === 'free' ? 'Upgrade Your Plan' : 'Plan Comparison'}
        </h2>

        <div className="grid md:grid-cols-3 gap-6">
          {Object.entries(SUBSCRIPTION_TIERS).map(([key, tier]) => {
            const isCurrentPlan = key === currentTier
            const isPro = key === 'pro'

            return (
              <Card 
                key={key} 
                className={`glass-card ${isPro ? 'border-blue-500/50' : ''} ${isCurrentPlan ? 'ring-2 ring-white/20' : ''}`}
              >
                {isPro && (
                  <div className="text-center -mt-4 mb-4">
                    <Badge className="bg-gradient-to-r from-blue-500 to-purple-500 text-white border-0">
                      Most Popular
                    </Badge>
                  </div>
                )}

                <CardHeader>
                  <CardTitle className="text-white">{tier.displayName}</CardTitle>
                  <CardDescription className="text-gray-400">
                    {key === 'free' && 'Perfect for getting started'}
                    {key === 'pro' && 'For growing contractors'}
                    {key === 'enterprise' && 'For large teams'}
                  </CardDescription>
                </CardHeader>

                <CardContent>
                  <div className="mb-6">
                    <div className="text-3xl font-bold text-white">
                      {tier.price === -1 ? 'Custom' : `$${tier.price}`}
                      {tier.price !== -1 && (
                        <span className="text-sm font-normal text-gray-400">/month</span>
                      )}
                    </div>
                  </div>

                  <ul className="space-y-3 mb-6">
                    {tier.features.map((feature, index) => (
                      <li key={index} className="flex items-start gap-2">
                        <Check className="h-5 w-5 text-green-400 flex-shrink-0 mt-0.5" />
                        <span className="text-gray-300 text-sm">{feature}</span>
                      </li>
                    ))}
                  </ul>

                  {isCurrentPlan ? (
                    <Button className="w-full" disabled variant="outline">
                      Current Plan
                    </Button>
                  ) : key === 'enterprise' ? (
                    <Button className="w-full" variant="outline">
                      Contact Sales
                    </Button>
                  ) : key === 'pro' && currentTier === 'free' ? (
                    <Button 
                      className="w-full bg-gradient-to-r from-blue-500 to-purple-500 hover:from-blue-600 hover:to-purple-600"
                      onClick={() => handleUpgrade('pro')}
                      disabled={processingUpgrade}
                    >
                      {processingUpgrade ? 'Processing...' : 'Upgrade to Pro'}
                      <ChevronRight className="h-4 w-4 ml-2" />
                    </Button>
                  ) : null}
                </CardContent>
              </Card>
            )
          })}
        </div>
      </div>

      {/* Billing History */}
      {currentTier !== 'free' && (
        <Card className="glass-card">
          <CardHeader>
            <CardTitle className="text-white">Billing History</CardTitle>
            <CardDescription className="text-gray-400">
              Your recent transactions
            </CardDescription>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              <div className="flex items-center justify-between p-4 glass-card rounded-lg">
                <div className="flex items-center gap-4">
                  <div className="w-10 h-10 bg-green-500/20 rounded-full flex items-center justify-center">
                    <Check className="h-5 w-5 text-green-400" />
                  </div>
                  <div>
                    <p className="text-white font-medium">Pro Plan</p>
                    <p className="text-sm text-gray-400">January 1, 2025</p>
                  </div>
                </div>
                <div className="text-white font-medium">$79.00</div>
              </div>
            </div>
          </CardContent>
        </Card>
      )}
    </div>
  )
}

