import { MetadataRoute } from 'next'

interface SitemapEntry {
  url: string
  lastModified: Date
  changeFrequency: 'always' | 'hourly' | 'daily' | 'weekly' | 'monthly' | 'yearly' | 'never'
  priority: number
}

export default function sitemap(): MetadataRoute.Sitemap {
  const baseUrl = process.env.NEXT_PUBLIC_SITE_URL || 'https://paintquotepro.com'
  const now = new Date()
  const lastWeek = new Date(now.getTime() - 7 * 24 * 60 * 60 * 1000)
  const lastMonth = new Date(now.getTime() - 30 * 24 * 60 * 60 * 1000)

  // Static pages with high priority
  const staticPages: SitemapEntry[] = [
    {
      url: baseUrl,
      lastModified: now,
      changeFrequency: 'daily',
      priority: 1.0
    },
    {
      url: `${baseUrl}/trial-signup`,
      lastModified: now,
      changeFrequency: 'weekly',
      priority: 0.95
    },
    {
      url: `${baseUrl}/guides`,
      lastModified: now,
      changeFrequency: 'weekly',
      priority: 0.9
    }
  ]

  // Pillar pages - highest priority content
  const pillarPages: SitemapEntry[] = [
    {
      url: `${baseUrl}/guides/how-to-quote-painting-jobs`,
      lastModified: now,
      changeFrequency: 'monthly',
      priority: 1.0
    },
    {
      url: `${baseUrl}/guides/painting-estimate-software`,
      lastModified: now,
      changeFrequency: 'monthly',
      priority: 1.0
    },
    {
      url: `${baseUrl}/guides/paint-calculator`,
      lastModified: now,
      changeFrequency: 'monthly',
      priority: 1.0
    },
    {
      url: `${baseUrl}/guides/painting-business-guide`,
      lastModified: now,
      changeFrequency: 'monthly',
      priority: 1.0
    }
  ]

  // Cluster pages for SEO pyramid
  const clusterPages: SitemapEntry[] = [
    // How to Quote cluster
    {
      url: `${baseUrl}/guides/interior-painting-quotes`,
      lastModified: now,
      changeFrequency: 'monthly',
      priority: 0.8
    },
    {
      url: `${baseUrl}/guides/exterior-painting-quotes`,
      lastModified: now,
      changeFrequency: 'monthly',
      priority: 0.8
    },
    {
      url: `${baseUrl}/guides/commercial-painting-quotes`,
      lastModified: now,
      changeFrequency: 'monthly',
      priority: 0.8
    },
    {
      url: `${baseUrl}/guides/cabinet-painting-quotes`,
      lastModified: now,
      changeFrequency: 'monthly',
      priority: 0.8
    },
    {
      url: `${baseUrl}/guides/paint-quantity-calculations`,
      lastModified: now,
      changeFrequency: 'monthly',
      priority: 0.8
    },
    {
      url: `${baseUrl}/guides/labor-cost-estimation`,
      lastModified: now,
      changeFrequency: 'monthly',
      priority: 0.8
    },
    {
      url: `${baseUrl}/guides/pricing-psychology`,
      lastModified: now,
      changeFrequency: 'monthly',
      priority: 0.8
    },
    {
      url: `${baseUrl}/guides/quote-presentation-tips`,
      lastModified: now,
      changeFrequency: 'monthly',
      priority: 0.8
    },
    {
      url: `${baseUrl}/guides/follow-up-strategies`,
      lastModified: now,
      changeFrequency: 'monthly',
      priority: 0.8
    },
    {
      url: `${baseUrl}/guides/common-quoting-mistakes`,
      lastModified: now,
      changeFrequency: 'monthly',
      priority: 0.8
    },
    // Paint Calculator cluster
    {
      url: `${baseUrl}/guides/paint-coverage-rates`,
      lastModified: now,
      changeFrequency: 'monthly',
      priority: 0.8
    },
    {
      url: `${baseUrl}/guides/measurement-techniques`,
      lastModified: now,
      changeFrequency: 'monthly',
      priority: 0.8
    }
  ]

  // Marketing pages
  const marketingPages: SitemapEntry[] = [
    {
      url: `${baseUrl}/features`,
      lastModified: now,
      changeFrequency: 'monthly',
      priority: 0.9
    },
    {
      url: `${baseUrl}/pricing`,
      lastModified: now,
      changeFrequency: 'monthly',
      priority: 0.9
    },
    {
      url: `${baseUrl}/about`,
      lastModified: now,
      changeFrequency: 'monthly',
      priority: 0.7
    }
  ]


  // Legal pages
  const legalPages: SitemapEntry[] = [
    {
      url: `${baseUrl}/privacy`,
      lastModified: now,
      changeFrequency: 'yearly',
      priority: 0.3
    },
    {
      url: `${baseUrl}/terms`,
      lastModified: now,
      changeFrequency: 'yearly',
      priority: 0.3
    }
  ]

  // Combine all pages
  const allPages = [
    ...staticPages,
    ...pillarPages,
    ...clusterPages,
    ...marketingPages,
    ...legalPages
  ]

  return allPages.map(page => ({
    url: page.url,
    lastModified: page.lastModified,
    changeFrequency: page.changeFrequency,
    priority: page.priority
  }))
}

// Generate dynamic sitemaps for programmatic SEO
export async function generateProgrammaticSitemap(): Promise<SitemapEntry[]> {
  const baseUrl = process.env.NEXT_PUBLIC_SITE_URL || 'https://paintquotepro.com'
  const now = new Date()
  const pages: SitemapEntry[] = []

  // Major US cities for location-based pages
  const cities = [
    { name: 'New York', state: 'NY' },
    { name: 'Los Angeles', state: 'CA' },
    { name: 'Chicago', state: 'IL' },
    { name: 'Houston', state: 'TX' },
    { name: 'Phoenix', state: 'AZ' },
    { name: 'Philadelphia', state: 'PA' },
    { name: 'San Antonio', state: 'TX' },
    { name: 'San Diego', state: 'CA' },
    { name: 'Dallas', state: 'TX' },
    { name: 'San Jose', state: 'CA' }
  ]

  // Generate location-based pages
  cities.forEach(city => {
    const citySlug = city.name.toLowerCase().replace(/\s+/g, '-')
    const stateSlug = city.state.toLowerCase()

    pages.push({
      url: `${baseUrl}/painting-contractors/${stateSlug}/${citySlug}`,
      lastModified: now,
      changeFrequency: 'monthly',
      priority: 0.5
    })
  })

  // Generate comparison pages
  const competitors = [
    'contractor-foreman',
    'jobber',
    'housecall-pro',
    'servicetitan',
    'jobprogress'
  ]

  competitors.forEach(competitor => {
    pages.push({
      url: `${baseUrl}/vs/${competitor}`,
      lastModified: now,
      changeFrequency: 'monthly',
      priority: 0.6
    })
  })

  // Generate integration pages
  const integrations = [
    'quickbooks',
    'stripe',
    'google-calendar',
    'zapier',
    'slack'
  ]

  integrations.forEach(integration => {
    pages.push({
      url: `${baseUrl}/integrations/${integration}`,
      lastModified: now,
      changeFrequency: 'monthly',
      priority: 0.4
    })
  })

  return pages
}

'use client';

import { useState } from 'react';
import { useRouter } from 'next/navigation';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { ArrowLeft, CheckCircle, Sparkles, Mail, Key } from 'lucide-react';
import Link from 'next/link';
import ModernNavigation from '@/components/modern-navigation';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';

export default function TrialSignupPage() {
  const [companyName, setCompanyName] = useState('');
  const [email, setEmail] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState(false);
  const [accessCode, setAccessCode] = useState('');
  const [signupMethod, setSignupMethod] = useState<'instant' | 'magic'>('magic');
  const [magicLinkSent, setMagicLinkSent] = useState(false);
  const router = useRouter();

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      if (signupMethod === 'magic') {
        // Magic link signup
        const response = await fetch('/api/auth/magic-signup', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ companyName, email }),
        });

        const data = await response.json();

        if (!response.ok) {
          throw new Error(data.error || 'Failed to send magic link');
        }

        setMagicLinkSent(true);
      } else {
        // Instant access signup (original method)
        const response = await fetch('/api/auth/simple-signup', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ companyName, email }),
        });

        const data = await response.json();

        if (!response.ok) {
          throw new Error(data.error || 'Failed to create account');
        }

        setSuccess(true);
        setAccessCode(data.accessCode);

        // Store company data in localStorage for authentication
        localStorage.setItem(
          'paintquote_company',
          JSON.stringify({
            id: data.company.id,
            accessCode: data.accessCode,
            name: data.company.name,
            phone: '',
            email: data.company.email || email,
            logoUrl: null,
            loginTime: Date.now(),
            isNewCompany: true,
            quotesRemaining: data.company.quotesRemaining || 5,
            onboarding_completed: false // Show setup prompt
          })
        );

        // Redirect to dashboard after 3 seconds
        setTimeout(() => {
          window.location.href = '/dashboard';
        }, 3000);
      }
    } catch (err) {
      setError(err instanceof Error ? err.message : 'An error occurred');
    } finally {
      setLoading(false);
    }
  };

  if (magicLinkSent) {
    return (
      <>
        <ModernNavigation />
        <div className="min-h-screen bg-gradient-to-br from-gray-900 via-gray-800 to-gray-900 flex items-center justify-center px-4 pt-16">
          {/* Background effects */}
          <div className="absolute inset-0">
            <div className="absolute top-20 left-10 w-72 h-72 bg-blue-500 rounded-full opacity-10 blur-3xl"></div>
            <div className="absolute bottom-20 right-10 w-96 h-96 bg-purple-500 rounded-full opacity-10 blur-3xl"></div>
          </div>

