interface AchievementNotificationProps {
  achievementId: string | null
  onClose: () => void
}

export function AchievementNotification({ achievementId, onClose }: AchievementNotificationProps) {
  const [show, setShow] = useState(false)

  useEffect(() => {
    if (achievementId) {
      setShow(true)

      // Trigger confetti
      const count = 200
      const defaults = {
        origin: { y: 0.7 }
      }

      function fire(particleRatio: number, opts: any) {
        confetti({
          ...defaults,
          ...opts,
          particleCount: Math.floor(count * particleRatio)
        })
      }

      fire(0.25, {
        spread: 26,
        startVelocity: 55,
      })
      fire(0.2, {
        spread: 60,
      })
      fire(0.35, {
        spread: 100,
        decay: 0.91,
        scalar: 0.8
      })
      fire(0.1, {
        spread: 120,
        startVelocity: 25,
        decay: 0.92,
        scalar: 1.2
      })
      fire(0.1, {
        spread: 120,
        startVelocity: 45,
      })

      // Auto-hide after 5 seconds
      const timer = setTimeout(() => {
        setShow(false)
        setTimeout(onClose, 300)
      }, 5000)

      return () => clearTimeout(timer)
    }
  }, [achievementId, onClose])

  if (!achievementId || !achievements[achievementId]) return null

  const achievement = achievements[achievementId]
  const Icon = achievement.icon

  return (
    <AnimatePresence>
      {show && (
        <motion.div
          initial={{ scale: 0, opacity: 0, y: 50 }}
          animate={{ scale: 1, opacity: 1, y: 0 }}
          exit={{ scale: 0, opacity: 0, y: 50 }}
          transition={{ type: 'spring', duration: 0.5 }}
          className="fixed top-20 right-4 z-50 max-w-sm"
        >
          <div className="glass-card p-6 border-2 border-white/20">
            <div className="flex items-start gap-4">
              <motion.div
                initial={{ rotate: -180, scale: 0 }}
                animate={{ rotate: 0, scale: 1 }}
                transition={{ delay: 0.2, type: 'spring' }}
                className={`w-16 h-16 rounded-full bg-gradient-to-br ${achievement.color} flex items-center justify-center flex-shrink-0`}
              >
                <Icon className="h-8 w-8 text-white" />
              </motion.div>
              <div className="flex-1">
                <motion.h3 
                  initial={{ x: -20, opacity: 0 }}
                  animate={{ x: 0, opacity: 1 }}
                  transition={{ delay: 0.3 }}
                  className="text-lg font-bold text-white mb-1"
                >
                  Achievement Unlocked!
                </motion.h3>
                <motion.p 
                  initial={{ x: -20, opacity: 0 }}
                  animate={{ x: 0, opacity: 1 }}
                  transition={{ delay: 0.4 }}
                  className="text-xl font-semibold text-white mb-1"
                >
                  {achievement.name}
                </motion.p>
                <motion.p 
                  initial={{ x: -20, opacity: 0 }}
                  animate={{ x: 0, opacity: 1 }}
                  transition={{ delay: 0.5 }}
                  className="text-sm text-gray-300"
                >
                  {achievement.description}
                </motion.p>
                <motion.div 
                  initial={{ scale: 0 }}
                  animate={{ scale: 1 }}
                  transition={{ delay: 0.6, type: 'spring' }}
                  className="mt-3 inline-flex items-center gap-2 bg-white/10 rounded-full px-3 py-1"
                >
                  <Star className="h-4 w-4 text-yellow-400" />
                  <span className="text-sm font-medium text-yellow-400">+{achievement.points} XP</span>
                </motion.div>
              </div>
            </div>
          </div>
        </motion.div>
      )}
    </AnimatePresence>
  )
}

'use client';

import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Progress } from '@/components/ui/progress';
import { 
  Calendar, 
  CreditCard, 
  FileText, 
  TrendingUp, 
  AlertCircle,
  Settings,
  ExternalLink
} from 'lucide-react';
import { format } from 'date-fns';

interface BillingOverviewProps {
  subscription: {
    id: string;
    status: string;
    plan: 'professional' | 'business';
    billingPeriod: 'monthly' | 'yearly';
    currentPeriodStart: Date;
    currentPeriodEnd: Date;
    cancelAtPeriodEnd: boolean;
  } | null;
  usage: {
    quotesThisMonth: number;
    quotesLimit: number;
    plan: 'professional' | 'business';
  };
  onManageBilling: () => void;
  onUpgrade: () => void;
  isLoading?: boolean;
}

export function BillingOverview({ 
  subscription, 
  usage, 
  onManageBilling, 
  onUpgrade, 
  isLoading 
}: BillingOverviewProps) {
  const getStatusColor = (status: string) => {
    switch (status) {
      case 'active': return 'bg-green-100 text-green-800';
      case 'past_due': return 'bg-yellow-100 text-yellow-800';
      case 'canceled': return 'bg-red-100 text-red-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const getUsagePercentage = () => {
    if (usage.quotesLimit === -1) return 0; // Unlimited
    return (usage.quotesThisMonth / usage.quotesLimit) * 100;
  };

  const getUsageColor = () => {
    const percentage = getUsagePercentage();
    if (percentage >= 90) return 'bg-red-500';
    if (percentage >= 75) return 'bg-yellow-500';
    return 'bg-blue-500';
  };

  return (
    <div className="space-y-6">
      {/* Current Subscription */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <CreditCard className="w-5 h-5" />
            Current Subscription
          </CardTitle>
        </CardHeader>
        <CardContent>
          {subscription ? (
            <div className="space-y-4">
              <div className="flex items-center justify-between">
                <div>
                  <h3 className="font-semibold capitalize">
                    {subscription.plan} Plan
                  </h3>
                  <p className="text-sm text-gray-600 capitalize">
                    {subscription.billingPeriod} billing
                  </p>
                </div>
                <Badge className={getStatusColor(subscription.status)}>
                  {subscription.status}
                </Badge>
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div>
                  <p className="text-sm text-gray-600">Current Period</p>
                  <p className="font-medium">
                    {format(subscription.currentPeriodStart, 'MMM d')} - {format(subscription.currentPeriodEnd, 'MMM d, yyyy')}
                  </p>
                </div>
                <div>
                  <p className="text-sm text-gray-600">Next Billing</p>
                  <p className="font-medium">
                    {subscription.cancelAtPeriodEnd 
                      ? 'Canceled' 
                      : format(subscription.currentPeriodEnd, 'MMM d, yyyy')
                    }
                  </p>
                </div>
              </div>

              {subscription.cancelAtPeriodEnd && (
                <div className="flex items-center gap-2 p-3 bg-yellow-50 rounded-lg">
                  <AlertCircle className="w-4 h-4 text-yellow-600" />
                  <span className="text-sm text-yellow-800">
                    Your subscription will end on {format(subscription.currentPeriodEnd, 'MMM d, yyyy')}
                  </span>
                </div>
              )}

              <div className="flex gap-2">
                <Button 
                  onClick={onManageBilling}
                  disabled={isLoading}
                  variant="outline"
                  className="flex-1"
                >
                  <Settings className="w-4 h-4 mr-2" />
                  Manage Billing
                </Button>
                {subscription.plan === 'professional' && (
                  <Button 
                    onClick={onUpgrade}
                    disabled={isLoading}
                    className="flex-1"
                  >
                    <TrendingUp className="w-4 h-4 mr-2" />
                    Upgrade to Business
                  </Button>
                )}
              </div>
            </div>
          ) : (
            <div className="text-center py-6">
              <p className="text-gray-600 mb-4">No active subscription</p>
              <Button onClick={onUpgrade} disabled={isLoading}>
                Choose a Plan
              </Button>
            </div>
          )}
        </CardContent>
      </Card>

      {/* Usage Overview */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <FileText className="w-5 h-5" />
            Usage This Month
          </CardTitle>
          <CardDescription>
            Track your quote generation usage
          </CardDescription>
        </CardHeader>
        <CardContent>
          <div className="space-y-4">
            <div className="flex items-center justify-between">
              <span className="text-sm font-medium">Quotes Generated</span>
              <span className="text-lg font-bold">
                {usage.quotesThisMonth}
                {usage.quotesLimit !== -1 && (
                  <span className="text-sm font-normal text-gray-600">
                    /{usage.quotesLimit}
                  </span>
                )}
              </span>
            </div>

            {usage.quotesLimit !== -1 && (
              <div className="space-y-2">
                <Progress 
                  value={getUsagePercentage()} 
                  className="h-2"
                />
                <div className="flex justify-between text-xs text-gray-600">
                  <span>0</span>
                  <span>{usage.quotesLimit} limit</span>
                </div>
              </div>
            )}

            {usage.quotesLimit === -1 && (
              <div className="flex items-center gap-2 text-sm text-green-600">
                <TrendingUp className="w-4 h-4" />
                <span>Unlimited quotes</span>
              </div>
            )}

            {usage.quotesLimit !== -1 && usage.quotesThisMonth >= usage.quotesLimit * 0.8 && (
              <div className="flex items-center gap-2 p-3 bg-yellow-50 rounded-lg">
                <AlertCircle className="w-4 h-4 text-yellow-600" />
                <span className="text-sm text-yellow-800">
                  You're approaching your monthly limit. Consider upgrading for unlimited quotes.
                </span>
              </div>
            )}
          </div>
        </CardContent>
      </Card>

      {/* Billing History */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Calendar className="w-5 h-5" />
            Billing History
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="text-center py-6">
            <p className="text-gray-600 mb-4">
              Access your complete billing history and download invoices
            </p>
            <Button 
              onClick={onManageBilling}
              disabled={isLoading}
              variant="outline"
            >
              <ExternalLink className="w-4 h-4 mr-2" />
              View Billing Portal
            </Button>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

