import { Send, Mic, Image } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { cn } from '@/lib/utils';

interface ChatInputProps {
  onSendMessage: (message: string) => void;
  isLoading?: boolean;
  placeholder?: string;
  suggestedReplies?: string[];
}

export function ChatInput({
  onSendMessage,
  isLoading,
  placeholder = 'Type your message...',
  suggestedReplies = []
}: ChatInputProps) {
  const [message, setMessage] = useState('');

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (message.trim() && !isLoading) {
      onSendMessage(message.trim());
      setMessage('');
    }
  };

  const handleSuggestedReply = (reply: string) => {
    if (!isLoading) {
      onSendMessage(reply);
    }
  };

  return (
    <div className="border-t border-white/10 bg-gray-900/50 backdrop-blur-sm">
      {/* Suggested replies */}
      {suggestedReplies.length > 0 && (
        <div className="flex flex-wrap gap-2 border-b border-white/10 p-3">
          {suggestedReplies.map((reply, index) => (
            <Button
              key={index}
              variant="outline"
              size="sm"
              onClick={() => handleSuggestedReply(reply)}
              disabled={isLoading}
              className="text-sm bg-white/5 border-white/20 text-gray-300 hover:bg-white/10 hover:text-white hover:border-white/30"
            >
              {reply}
            </Button>
          ))}
        </div>
      )}

      {/* Input form */}
      <form onSubmit={handleSubmit} className="flex items-center gap-2 p-4">
        <Button
          type="button"
          variant="ghost"
          size="icon"
          className="shrink-0 text-gray-400 hover:text-gray-200 hover:bg-white/10"
          disabled
          title="Voice input coming soon"
        >
          <Mic className="h-5 w-5" />
        </Button>

        <Button
          type="button"
          variant="ghost"
          size="icon"
          className="shrink-0 text-gray-400 hover:text-gray-200 hover:bg-white/10"
          disabled
          title="Image upload coming soon"
        >
          <Image className="h-5 w-5" />
        </Button>

        <input
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          placeholder={placeholder}
          disabled={isLoading}
          className="flex-1 bg-white/10 border border-white/20 rounded-lg px-4 py-2 text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-blue-400/50 focus:border-transparent disabled:opacity-50 disabled:cursor-not-allowed"
          autoFocus
        />

        <Button
          type="submit"
          size="icon"
          disabled={!message.trim() || isLoading}
          className={cn(
            'shrink-0 transition-all',
            message.trim() && !isLoading
              ? 'bg-gradient-to-r from-blue-500 to-blue-600 hover:from-blue-600 hover:to-blue-700 text-white shadow-lg shadow-blue-500/25'
              : 'bg-white/10 text-gray-400'
          )}
        >
          <Send className="h-5 w-5" />
        </Button>
      </form>
    </div>
  );
}

'use client';

import { useState, useEffect, useRef } from 'react';
import { useRouter } from 'next/navigation';
import { MessageBubble } from './message-bubble';
import { ChatInput } from './chat-input';
import { Card } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { AlertCircle, CheckCircle } from 'lucide-react';
import { toast } from '@/components/ui/use-toast';
import { useAchievements } from '@/hooks/use-achievements';
import { AchievementNotification } from '@/components/achievements/achievement-notification';
import { redirectToStripePayment } from '@/lib/config/stripe-links';

interface Message {
  role: 'user' | 'assistant';
  content: string;
  timestamp: Date;
}

interface ChatInterfaceProps {
  companyId: number;
  isDemo?: boolean;
  onQuoteCreated?: (quoteId: string) => void;
}

export function ChatInterface({ 
  companyId, 
  isDemo = false,
  onQuoteCreated
}: ChatInterfaceProps) {
  const [messages, setMessages] = useState<Message[]>([]);
  const [isLoading, setIsLoading] = useState(false);
  const [sessionId, setSessionId] = useState<string | null>(null);
  const [quoteData, setQuoteData] = useState<any>(null);
  const [suggestedReplies, setSuggestedReplies] = useState<string[]>([]);
  const [startTime] = useState(Date.now());
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const router = useRouter();
  const { latestAchievement, checkQuoteAchievements } = useAchievements();

  // Scroll to bottom when messages change
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  // Initial greeting
  useEffect(() => {
    if (isDemo) {
      // Demo mode - pre-fill with demo data
      setMessages([
        {
          role: 'assistant',
          content: "🎯 **Demo Mode**: Let me show you how fast you can create professional quotes! I'll walk you through a typical residential project.",
          timestamp: new Date()
        }
      ]);
      // Auto-start the demo after a short delay
      setTimeout(() => {
        handleDemoStart();
      }, 1500);
    } else {
      setMessages([
        {
          role: 'assistant',
          content: "Hi! I'll help you create a professional quote for your customer. What type of painting project are you quoting today?",
          timestamp: new Date()
        }
      ]);
      // Set initial suggested replies
      setSuggestedReplies(['Interior residential', 'Exterior residential', 'Commercial space', 'Single room', 'Whole house', 'Office space']);
    }
  }, [isDemo]);

  const handleDemoStart = async () => {
    // Simulate typing indicator
    setIsLoading(true);

    // First user message
    setTimeout(() => {
      setMessages(prev => [...prev, {
        role: 'user',
        content: "I need a quote for painting a 3-bedroom house interior. The client is Sarah Johnson at 123 Maple Street.",
        timestamp: new Date()
      }]);
      setIsLoading(false);
    }, 500);

    // Assistant response
    setTimeout(() => {
      setIsLoading(true);
      setTimeout(() => {
        setMessages(prev => [...prev, {
          role: 'assistant',
          content: "Great! I'll help you create a quote for Sarah Johnson's interior painting project. Let me gather a few more details. What's the approximate square footage of the home?",
          timestamp: new Date()
        }]);
        setSuggestedReplies(['1,500 sq ft', '2,000 sq ft', '2,500 sq ft', '3,000 sq ft']);
        setIsLoading(false);
      }, 1000);
    }, 1500);

    // Auto-continue demo
    setTimeout(() => {
      sendMessage("It's about 2,000 sq ft with standard 8-foot ceilings");
    }, 5000);
  };

  const sendMessage = async (content: string) => {
    // Add user message
    const userMessage: Message = {
      role: 'user',
      content,
      timestamp: new Date()
    };
    setMessages(prev => [...prev, userMessage]);
    setIsLoading(true);
    setSuggestedReplies([]);

    try {
      // Get company data from localStorage for access code
      const companyData = localStorage.getItem('paintquote_company');
      const company = companyData ? JSON.parse(companyData) : null;

      const response = await fetch('/api/chat', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'x-company-data': JSON.stringify({ 
            id: companyId,
            access_code: company?.accessCode || ''
          })
        },
        body: JSON.stringify({
          message: content,
          sessionId,
          isDemo: isDemo
        })
      });

      let data;
      try {
        const text = await response.text();
        data = JSON.parse(text);
      } catch (parseError) {
        console.error('Failed to parse response:', parseError);
        console.error('Response status:', response.status);
        console.error('Response headers:', response.headers);
        throw new Error(`Server error: ${response.status} - Unable to parse response`);
      }

      if (!response.ok) {
        // Check if it's a subscription upgrade required error
        if (response.status === 403 && data.requiresUpgrade) {
          toast({
            title: 'Upgrade Required',
            description: data.message || 'You\'ve reached your monthly quote limit.',
            action: (
              <Button 
                size="sm" 
                onClick={() => redirectToStripePayment('professional', 'monthly')}
              >
                Upgrade Now
              </Button>
            )
          });

          // Add a message to the chat
          const upgradeMessage: Message = {
            role: 'assistant',
            content: '🔒 ' + (data.message || 'You\'ve reached your monthly AI quote limit. Please upgrade to Pro for unlimited quotes!'),
            timestamp: new Date()
          };
          setMessages(prev => [...prev, upgradeMessage]);
          return;
        }
        throw new Error(data.error || 'Failed to send message');
      }

      // Add assistant response
      const assistantMessage: Message = {
        role: 'assistant',
        content: data.response,
        timestamp: new Date()
      };
      setMessages(prev => [...prev, assistantMessage]);

      // Update session and other data
      if (data.sessionId) setSessionId(data.sessionId);
      if (data.suggestedReplies) setSuggestedReplies(data.suggestedReplies);

      // Handle quote completion
      if (data.isComplete && data.quoteData) {
        setQuoteData(data.quoteData);
      }

    } catch (error) {
      console.error('Chat error:', error);
      toast({
        title: 'Error',
        description: 'Failed to send message. Please try again.',
        variant: 'destructive'
      });
    } finally {
      setIsLoading(false);
    }
  };

  const createQuote = async () => {
    if (!quoteData) return;

    console.log('[CHAT] Quote data from AI:', quoteData);
    console.log('[CHAT] Pricing structure:', quoteData.pricing);

    // Debug: Show what we're about to send
    console.log('[CHAT DEBUG] Creating quote with data:', {
      customerName: quoteData.customerName,
      hasPricing: !!quoteData.pricing,
      pricingKeys: quoteData.pricing ? Object.keys(quoteData.pricing) : 'no pricing',
      pricingStructure: quoteData.pricing
    });

    setIsLoading(true);
    try {
      // Get company data from localStorage for access code
      const companyData = localStorage.getItem('paintquote_company');
      const company = companyData ? JSON.parse(companyData) : null;

      // Check if pricing data exists
      if (!quoteData.pricing) {
        console.error('[CHAT] No pricing data in quote:', quoteData);
        toast({
          title: 'Incomplete Quote',
          description: 'The quote is missing pricing information. Please try again.',
          variant: 'destructive'
        });
        return;
      }

      const requestBody = {
        companyId: Number(companyId), // Ensure it's a number
        quoteData: {
          customerName: quoteData.customerName || 'Unknown Customer',
          customerEmail: quoteData.customerEmail || null,
          customerPhone: quoteData.customerPhone || null,
          address: quoteData.address || null,
          projectType: quoteData.projectType || 'interior',
          rooms: quoteData.rooms || [],
          roomCount: quoteData.roomCount || 0,
          paintQuality: quoteData.paintQuality || 'better',
          prepWork: quoteData.prepWork || null,
          timeEstimate: quoteData.timeline || null,
          specialRequests: quoteData.specialRequests || null,
          totalCost: quoteData.pricing?.subtotal || quoteData.pricing?.total || 0,
          finalPrice: quoteData.pricing?.total || quoteData.pricing?.subtotal || 0,
          markupPercentage: quoteData.markupPercentage || 30,
          sqft: quoteData.surfaces?.walls || quoteData.measurements?.wallSqft || quoteData.sqft || 0,
          breakdown: {
            materials: quoteData.pricing?.materials?.total || quoteData.pricing?.materials || 0,
            labor: quoteData.pricing?.labor?.total || quoteData.pricing?.labor || 0,
            markup: quoteData.pricing?.markup || 0
          }
        },
        conversationHistory: messages
      };

      console.log('[CHAT] Request body to quotes API:', requestBody);
      console.log('[CHAT] Conversation history length:', messages.length);
      console.log('[CHAT] Request body size:', new Blob([JSON.stringify(requestBody)]).size, 'bytes');

      // Limit conversation history to prevent oversized requests
      const limitedMessages = messages.slice(-10); // Keep only last 10 messages
      const finalRequestBody = {
        ...requestBody,
        conversationHistory: limitedMessages
      };

      const response = await fetch('/api/quotes', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'x-company-data': JSON.stringify({ 
            id: companyId,
            access_code: company?.accessCode || ''
          })
        },
        body: JSON.stringify(finalRequestBody)
      });

      let result;
      try {
        const text = await response.text();
        console.log('[CHAT] Quote API response text:', text);

        // Handle empty response
        if (!text) {
          console.error('Empty response from quote API');
          throw new Error('Server returned empty response. Check server logs.');
        }

        result = JSON.parse(text);
      } catch (parseError) {
        console.error('Failed to parse quote response:', parseError);
        console.error('Response status:', response.status);
        console.error('Response text length:', response.text?.length || 0);

        // Check for common server errors
        if (response.status === 500) {
          throw new Error('Server error: Database might not be initialized. Please check /api/diagnose for details.');
        }
        throw new Error(`Failed to parse quote response: ${response.status}`);
      }

      if (!response.ok) {
        console.error('Quote creation error response:', result);
        throw new Error(result.details || result.error || 'Failed to create quote');
      }

      toast({
        title: 'Quote Created!',
        description: `Quote ${result.quoteId} has been created successfully.`,
        action: (
          <Button 
            size="sm" 
            onClick={() => router.push(`/dashboard/quotes/${result.quoteId}`)}
          >
            View Quote
          </Button>
        )
      });

      // Check for achievements
      const timeToCreate = Date.now() - startTime;
      await checkQuoteAchievements(quoteData, timeToCreate);

      // Navigate to the quote using the quote_id (like Q-2025-00001-1NWTY8)
      if (onQuoteCreated) {
        onQuoteCreated(result.quoteId);
      } else {
        // Small delay to show the toast
        setTimeout(() => {
          router.push(`/dashboard/quotes/${result.quoteId}`);
        }, 1000);
      }

    } catch (error) {
      console.error('Create quote error:', error);
      toast({
        title: 'Error',
        description: 'Failed to create quote. Please try again.',
        variant: 'destructive'
      });
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="flex h-full flex-col bg-gray-900/50">
      {/* Messages area */}
      <div className="flex-1 overflow-y-auto p-4 space-y-4 bg-gradient-to-b from-transparent to-gray-900/30">
        {messages.map((message, index) => (
          <MessageBubble
            key={index}
            message={message}
          />
        ))}
        {isLoading && (
          <MessageBubble
            message={{ role: 'assistant', content: '' }}
            isTyping
          />
        )}
        <div ref={messagesEndRef} />
      </div>

      {/* Quote preview */}
      {quoteData && (
        <div className="mx-4 mb-4 p-6 bg-gradient-to-r from-green-500/10 to-emerald-500/10 backdrop-blur-sm rounded-xl border border-green-500/20">
          <div className="flex items-start gap-3">
            <CheckCircle className="h-5 w-5 text-green-400 mt-0.5" />
            <div className="flex-1">
              <h3 className="font-semibold text-green-300 text-lg">
                Quote Ready!
              </h3>
              <p className="text-sm text-green-400/80 mt-1">
                {quoteData.customerName} - {quoteData.address}
              </p>

              {/* Quote Breakdown */}
              {quoteData.pricing?.breakdown && (
                <div className="mt-4 space-y-3">
                  <div className="bg-white/10 backdrop-blur-sm rounded-lg p-4 border border-white/10">
                    <h4 className="font-semibold text-sm mb-2 text-white">MATERIALS: ${quoteData.pricing.materials?.total?.toFixed(2)}</h4>
                    <div className="space-y-1 text-sm text-gray-300">
                      {quoteData.pricing.breakdown.primer && (
                        <div>• Primer: {quoteData.pricing.breakdown.primer.gallons} gallons - ${quoteData.pricing.breakdown.primer.cost.toFixed(2)}</div>
                      )}
                      {quoteData.pricing.breakdown.wallPaint && (
                        <div>• Wall Paint: {quoteData.pricing.breakdown.wallPaint.gallons} gallons - ${quoteData.pricing.breakdown.wallPaint.cost.toFixed(2)}</div>
                      )}
                      {quoteData.pricing.breakdown.ceilingPaint && (
                        <div>• Ceiling Paint: {quoteData.pricing.breakdown.ceilingPaint.gallons} gallons - ${quoteData.pricing.breakdown.ceilingPaint.cost.toFixed(2)}</div>
                      )}
                      {quoteData.pricing.breakdown.supplies && (
                        <div>• Supplies: ${quoteData.pricing.breakdown.supplies.toFixed(2)}</div>
                      )}
                    </div>
                  </div>

                  <div className="bg-white/10 backdrop-blur-sm rounded-lg p-4 border border-white/10">
                    <h4 className="font-semibold text-sm mb-2 text-white">LABOR: ${quoteData.pricing.labor?.total?.toFixed(2)}</h4>
                    <div className="space-y-1 text-sm text-gray-300">
                      {quoteData.pricing.breakdown.prepWork && (
                        <div>• Prep work: {quoteData.pricing.breakdown.prepWork.hours} hours - ${quoteData.pricing.breakdown.prepWork.cost.toFixed(2)}</div>
                      )}
                      {quoteData.pricing.breakdown.painting && (
                        <div>• Painting: {quoteData.pricing.breakdown.painting.hours} hours - ${quoteData.pricing.breakdown.painting.cost.toFixed(2)}</div>
                      )}
                    </div>
                  </div>
                </div>
              )}

              <div className="mt-4 pt-4 border-t border-green-500/30">
                <div className="flex justify-between items-center">
                  <div>
                    <p className="text-2xl font-bold text-white">
                      Total: ${quoteData.pricing?.total?.toFixed(2) || '0.00'}
                    </p>
                    {quoteData.pricing?.timeline && (
                      <p className="text-sm text-green-400">
                        Timeline: {quoteData.pricing.timeline}
                      </p>
                    )}
                  </div>
                  <Button
                    onClick={createQuote}
                    disabled={isLoading}
                    size="lg"
                    className="bg-gradient-to-r from-green-500 to-emerald-500 hover:from-green-600 hover:to-emerald-600 text-white shadow-lg shadow-green-500/25"
                  >
                    Create Quote
                  </Button>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Input area */}
      <ChatInput
        onSendMessage={sendMessage}
        isLoading={isLoading}
        suggestedReplies={suggestedReplies}
        placeholder={
          quoteData 
            ? "Quote is ready! Click 'Create Quote' above."
            : "Type your message..."
        }
      />

      {/* Achievement Notifications */}
      <AchievementNotification 
        achievementId={latestAchievement}
        onClose={() => {}}
      />
    </div>
  );
}

'use client'

import { useState, useRef } from 'react'
import { Button } from '@/components/ui/button'
import { Label } from '@/components/ui/label'
import { Card } from '@/components/ui/card'
import { Upload, Building2, Palette, Check } from 'lucide-react'
import { toast } from '@/components/ui/use-toast'

interface ExtractedColors {
  primary: string
  secondary: string
  accent: string
  dark: string
  light: string
}

interface LogoUploadProps {
  currentLogoUrl?: string
  onLogoChange: (logoUrl: string, colors?: ExtractedColors) => void
  companyName: string
}

export function LogoUploadWithExtraction({ 
  currentLogoUrl, 
  onLogoChange,
  companyName 
}: LogoUploadProps) {
  const [isUploading, setIsUploading] = useState(false)
  const [extractedColors, setExtractedColors] = useState<ExtractedColors | null>(null)
  const [selectedTheme, setSelectedTheme] = useState<string>('default')
  const fileInputRef = useRef<HTMLInputElement>(null)

  const extractColors = async (imageUrl: string): Promise<ExtractedColors> => {
    // In production, this would use a library like Vibrant.js or Color Thief
    // For now, we'll simulate color extraction
    return new Promise((resolve) => {
      const img = new Image()
      img.crossOrigin = 'anonymous'

      img.onload = () => {
        const canvas = document.createElement('canvas')
        const ctx = canvas.getContext('2d')
        if (!ctx) return

        canvas.width = img.width
        canvas.height = img.height
        ctx.drawImage(img, 0, 0)

        // Simple color extraction - in production use proper algorithm
        const imageData = ctx.getImageData(0, 0, canvas.width, canvas.height)
        const pixels = imageData.data

        // Sample colors from different regions
        const colors: number[][] = []
        const sampleSize = Math.floor(pixels.length / 4 / 100) // Sample 1% of pixels

        for (let i = 0; i < pixels.length; i += 4 * sampleSize) {
          const r = pixels[i]
          const g = pixels[i + 1]
          const b = pixels[i + 2]
          const a = pixels[i + 3]

          if (a > 200) { // Only consider opaque pixels
            colors.push([r, g, b])
          }
        }

        // Sort by brightness and pick strategic colors
        colors.sort((a, b) => {
          const brightnessA = (a[0] + a[1] + a[2]) / 3
          const brightnessB = (b[0] + b[1] + b[2]) / 3
          return brightnessB - brightnessA
        })

        const extractedColors: ExtractedColors = {
          primary: rgbToHex(colors[Math.floor(colors.length * 0.3)] || [59, 130, 246]),
          secondary: rgbToHex(colors[Math.floor(colors.length * 0.5)] || [139, 92, 246]),
          accent: rgbToHex(colors[Math.floor(colors.length * 0.7)] || [16, 185, 129]),
          dark: rgbToHex(colors[Math.floor(colors.length * 0.9)] || [30, 41, 59]),
          light: rgbToHex(colors[Math.floor(colors.length * 0.1)] || [241, 245, 249])
        }

        resolve(extractedColors)
      }

      img.src = imageUrl
    })
  }

  const rgbToHex = (rgb: number[]): string => {
    return '#' + rgb.map(x => {
      const hex = x.toString(16)
      return hex.length === 1 ? '0' + hex : hex
    }).join('')
  }

  const handleFileSelect = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0]
    if (!file) return

    // Validate file type
    if (!file.type.startsWith('image/')) {
      toast({
        title: 'Invalid file type',
        description: 'Please upload an image file (PNG, JPG, etc.)',
        variant: 'destructive'
      })
      return
    }

    // Validate file size (max 5MB)
    if (file.size > 5 * 1024 * 1024) {
      toast({
        title: 'File too large',
        description: 'Please upload an image smaller than 5MB',
        variant: 'destructive'
      })
      return
    }

    setIsUploading(true)

    try {
      // Convert to base64 for preview and color extraction
      const reader = new FileReader()
      reader.onloadend = async () => {
        const base64 = reader.result as string

        // Extract colors
        const colors = await extractColors(base64)
        setExtractedColors(colors)

        // In production, upload to cloud storage here
        // For now, we'll use the base64 URL
        onLogoChange(base64, colors)

        toast({
          title: 'Logo uploaded successfully!',
          description: 'We\'ve extracted your brand colors from the logo.',
        })
      }

      reader.readAsDataURL(file)
    } catch (error) {
      toast({
        title: 'Upload failed',
        description: 'Failed to upload logo. Please try again.',
        variant: 'destructive'
      })
    } finally {
      setIsUploading(false)
    }
  }

  const themes = extractedColors ? [
    {
      name: 'Professional',
      preview: {
        header: extractedColors.primary,
        accent: extractedColors.secondary,
        text: extractedColors.dark,
        background: '#ffffff'
      }
    },
    {
      name: 'Modern',
      preview: {
        header: '#ffffff',
        accent: extractedColors.primary,
        text: extractedColors.dark,
        background: extractedColors.light
      }
    },
    {
      name: 'Bold',
      preview: {
        header: extractedColors.accent,
        accent: extractedColors.primary,
        text: '#ffffff',
        background: extractedColors.dark
      }
    }
  ] : []

  return (
    <div className="space-y-6">
      {/* Logo Upload */}
      <div>
        <Label>Company Logo</Label>
        <div className="mt-2 flex items-center gap-4">
          {currentLogoUrl ? (
            <img 
              src={currentLogoUrl} 
              alt="Company logo" 
              className="h-20 w-20 rounded-lg object-contain bg-white p-2" 
            />
          ) : (
            <div className="h-20 w-20 rounded-lg bg-muted flex items-center justify-center">
              <Building2 className="h-10 w-10 text-muted-foreground" />
            </div>
          )}
          <div>
            <input
              ref={fileInputRef}
              type="file"
              accept="image/*"
              onChange={handleFileSelect}
              className="hidden"
            />
            <Button 
              variant="outline" 
              size="sm"
