    ceiling?: { name: string; coverageRate: number; costPerGallon: number };
    trim?: { name: string; coverageRate: number; costPerGallon: number };
    primer?: { name: string; coverageRate: number; costPerGallon: number };
  };
  paintQuality?: 'good' | 'better' | 'best';
  laborRate?: number;  // per hour
  companyRates?: {
    paintingRate?: number;
    primingRate?: number;
    trimRate?: number;
    doorRate?: number;
    windowRate?: number;
    overheadPercent?: number;
    profitMargin?: number;
  };
  prepCondition?: 'good' | 'minor' | 'major';
  rushJob?: boolean;
  markupPercentage?: number;
  taxRate?: number;
}

export interface CalculatorOutput {
  materials: {
    paint: number;
    supplies: number;
    total: number;
  };
  labor: {
    hours: number;
    rate: number;
    total: number;
  };
  subtotal: number;
  markup: number;
  tax: number;
  total: number;
  timeline: string;
  breakdown: {
    walls?: { sqft: number; cost: number };
    ceilings?: { sqft: number; cost: number };
    trim?: { linearFt: number; cost: number };
    doors?: { count: number; cost: number };
    windows?: { count: number; cost: number };
    primer?: { gallons: number; product: string; cost: number };
    wallPaint?: { gallons: number; product: string; cost: number };
    ceilingPaint?: { gallons: number; product: string; cost: number };
    supplies?: number;
    prepWork?: { hours: number; cost: number };
    painting?: { hours: number; cost: number };
  };
}

export class QuoteCalculator {
  // Default rates and constants
  private static readonly DEFAULT_COVERAGE = 350; // sqft per gallon
  private static readonly DEFAULT_LABOR_RATE = 45; // per hour
  private static readonly SUPPLIES_BASE = 100; // base supplies cost
  private static readonly SUPPLIES_PERCENTAGE = 0.12; // 12% of materials for sundries
  private static readonly GALLON_MULTIPLIER = 1.8; // account for multiple coats

  // Paint costs per gallon by quality (fallback if no products specified)
  private static readonly PAINT_COSTS = {
    good: 25,
    better: 35,
    best: 50
  };

  // Default productivity rates (sqft per hour)
  private static readonly PRODUCTIVITY_RATES = {
    walls: 150,      // sqft per hour
    ceilings: 100,   // sqft per hour
    trim: 60,        // linear ft per hour
    doors: 2,        // hours per door
    windows: 3,      // hours per window
    priming: 200     // sqft per hour
  };

  // Prep work adjustments
  private static readonly PREP_ADJUSTMENTS = {
    good: 0,      // no additional prep
    minor: 0.10,  // 10% additional labor
    major: 0.25   // 25% additional labor
  };

  // Rush job adjustment
  private static readonly RUSH_ADJUSTMENT = 0.15; // 15% additional labor

  static calculate(input: CalculatorInput): CalculatorOutput {
    const breakdown: any = {};
    let materialsCost = 0;
    let laborHours = 0;

    // Calculate primer if needed
    if (input.surfaces.priming) {
      const primerCoverage = input.paintProducts?.primer?.coverageRate || this.DEFAULT_COVERAGE;
      const primerGallons = Math.ceil(input.surfaces.priming / primerCoverage);
      const primerCost = primerGallons * (input.paintProducts?.primer?.costPerGallon || 20);
      materialsCost += primerCost;
      breakdown.primer = {
        gallons: primerGallons,
        product: input.paintProducts?.primer?.name || 'Standard Primer',
        cost: primerCost
      };

      // Add priming labor
      laborHours += input.surfaces.priming / this.PRODUCTIVITY_RATES.priming;
    }

    // Calculate wall paint
    if (input.surfaces.walls) {
      const wallCoverage = input.paintProducts?.walls?.coverageRate || this.DEFAULT_COVERAGE;
      const wallGallons = Math.ceil((input.surfaces.walls / wallCoverage) * this.GALLON_MULTIPLIER);
      const wallPaintCost = wallGallons * (input.paintProducts?.walls?.costPerGallon || this.PAINT_COSTS[input.paintQuality || 'better']);
      materialsCost += wallPaintCost;
      breakdown.wallPaint = {
        gallons: wallGallons,
        product: input.paintProducts?.walls?.name || 'Wall Paint',
        cost: wallPaintCost
      };

      // Add wall painting labor
      laborHours += input.surfaces.walls / this.PRODUCTIVITY_RATES.walls;
    }

    // Calculate ceiling paint
    if (input.surfaces.ceilings) {
      const ceilingCoverage = input.paintProducts?.ceiling?.coverageRate || this.DEFAULT_COVERAGE;
      const ceilingGallons = Math.ceil((input.surfaces.ceilings / ceilingCoverage) * this.GALLON_MULTIPLIER);
      const ceilingPaintCost = ceilingGallons * (input.paintProducts?.ceiling?.costPerGallon || this.PAINT_COSTS[input.paintQuality || 'better']);
      materialsCost += ceilingPaintCost;
      breakdown.ceilingPaint = {
        gallons: ceilingGallons,
        product: input.paintProducts?.ceiling?.name || 'Ceiling Paint',
        cost: ceilingPaintCost
      };

      // Add ceiling painting labor
      laborHours += input.surfaces.ceilings / this.PRODUCTIVITY_RATES.ceilings;
    }

    // Add trim/door/window labor
    if (input.surfaces.trim) {
      laborHours += input.surfaces.trim / this.PRODUCTIVITY_RATES.trim;
    }
    if (input.surfaces.doors) {
      laborHours += input.surfaces.doors * this.PRODUCTIVITY_RATES.doors;
    }
    if (input.surfaces.windows) {
      laborHours += input.surfaces.windows * this.PRODUCTIVITY_RATES.windows;
    }

    // Calculate supplies
    const suppliesCost = this.SUPPLIES_BASE + (materialsCost * this.SUPPLIES_PERCENTAGE);
    breakdown.supplies = suppliesCost;
    materialsCost += suppliesCost;

    // Apply prep work adjustment
    let prepHours = 0;
    if (input.prepCondition && input.prepCondition !== 'good') {
      prepHours = laborHours * this.PREP_ADJUSTMENTS[input.prepCondition];
      breakdown.prepWork = {
        hours: Math.round(prepHours * 10) / 10,
        cost: prepHours * (input.laborRate || this.DEFAULT_LABOR_RATE)
      };
    }

    // Apply rush job adjustment
    if (input.rushJob) {
      laborHours += laborHours * this.RUSH_ADJUSTMENT;
    }

    // Calculate painting labor cost
    const paintingHours = laborHours;
    const laborRate = input.laborRate || (input.companyRates as any)?.hourlyRate || this.DEFAULT_LABOR_RATE;
    const paintingLaborCost = paintingHours * laborRate;
    const prepLaborCost = prepHours * laborRate;
    const totalLaborCost = paintingLaborCost + prepLaborCost;

    breakdown.painting = {
      hours: Math.round(paintingHours * 10) / 10,
      cost: paintingLaborCost
    };

    // Add surface breakdown for reference
    if (input.surfaces.walls) {
      breakdown.walls = { sqft: input.surfaces.walls, cost: 0 };
    }
    if (input.surfaces.ceilings) {
      breakdown.ceilings = { sqft: input.surfaces.ceilings, cost: 0 };
    }
    if (input.surfaces.trim) {
      breakdown.trim = { linearFt: input.surfaces.trim, cost: 0 };
    }
    if (input.surfaces.doors) {
      breakdown.doors = { count: input.surfaces.doors, cost: 0 };
    }
    if (input.surfaces.windows) {
      breakdown.windows = { count: input.surfaces.windows, cost: 0 };
    }

    // Calculate totals with company rates
    const subtotal = materialsCost + totalLaborCost;
    const overheadPercent = input.companyRates?.overheadPercent || 15;
    const profitMargin = input.companyRates?.profitMargin || input.markupPercentage || 30;

    // Apply overhead and profit
    const overhead = subtotal * (overheadPercent / 100);
    const profit = subtotal * (profitMargin / 100);
    const markup = overhead + profit;

    const beforeTax = subtotal + markup;
    const taxRate = (input.taxRate || 0) / 100;
    const tax = beforeTax * taxRate;
    const total = beforeTax + tax;

    // Estimate timeline
    const totalHours = paintingHours + prepHours;
    const hoursPerDay = 8;
    const daysNeeded = Math.ceil(totalHours / hoursPerDay);
    let timeline = '';
    if (daysNeeded <= 1) {
      timeline = '1 day';
    } else if (daysNeeded <= 3) {
      timeline = `${daysNeeded} days`;
    } else if (daysNeeded <= 5) {
      timeline = '1 week';
    } else {
      timeline = `${Math.ceil(daysNeeded / 5)} weeks`;
    }

    return {
      materials: {
        paint: materialsCost - suppliesCost,
        supplies: suppliesCost,
        total: materialsCost
      },
      labor: {
        hours: Math.round((paintingHours + prepHours) * 10) / 10,
        rate: laborRate,
        total: totalLaborCost
      },
      subtotal,
      markup,
      tax,
      total,
      timeline,
      breakdown
    };
  }

  static estimateFromRooms(rooms: any[], paintQuality?: 'good' | 'better' | 'best'): CalculatorInput {
    let totalWalls = 0;
    let totalCeilings = 0;
    let totalDoors = 0;
    let totalWindows = 0;
    let totalTrim = 0;

    rooms.forEach(room => {
      // Estimate wall area (perimeter * height)
      if (room.length && room.width && room.height) {
        const perimeter = 2 * (room.length + room.width);
        const wallArea = perimeter * room.height;
        totalWalls += wallArea;

        // Ceiling area
        const ceilingArea = room.length * room.width;
        totalCeilings += ceilingArea;

        // Estimate trim (baseboard)
        totalTrim += perimeter;
      }

      // Count doors and windows
      totalDoors += room.doors || 1;
      totalWindows += room.windows || 2;
    });

    // Subtract door and window area from walls
    const doorArea = totalDoors * 21; // 3x7 ft door
    const windowArea = totalWindows * 15; // 3x5 ft window
    totalWalls = Math.max(0, totalWalls - doorArea - windowArea);

    return {
      surfaces: {
        walls: Math.round(totalWalls),
        ceilings: Math.round(totalCeilings),
        trim: Math.round(totalTrim),
        doors: totalDoors,
        windows: totalWindows
      },
      paintQuality: paintQuality || 'better'
    };
  }
}

// Export singleton instance for convenience
export const calculator = new QuoteCalculator();

export interface ConversationStep {
  id: string;
  question: string;
  field: string;
  type: 'text' | 'number' | 'select' | 'multiselect';
  options?: string[];
  required: boolean;
  validation?: (value: any) => boolean;
  next?: (value: any) => string | null;
}

export interface ConversationState {
  currentStep: string;
  collectedData: Record<string, any>;
  messages: Array<{
    role: 'user' | 'assistant';
    content: string;
    timestamp: Date;
  }>;
  isComplete: boolean;
  quoteReady: boolean;
}

export class ConversationManager {
  private steps: Map<string, ConversationStep> = new Map();
  private state: ConversationState;

  constructor() {
    this.initializeSteps();
    this.state = this.createInitialState();
  }

  private initializeSteps() {
    // Define conversation flow based on research: 2-minute professional quote
    const steps: ConversationStep[] = [
      {
        id: 'start',
        question: "Hi! I'll help you build a quote in under 2 minutes. I see you're set up for painting with your standard rates. Let's start - what type of space are we quoting today?",
        field: 'spaceType',
        type: 'select',
        options: ['living room', 'bedroom', 'kitchen', 'bathroom', 'whole house', 'office', 'other'],
        required: true,
        next: (value) => value === 'whole house' ? 'houseSize' : 'roomDimensions'
      },
      // Step 1: Room Dimensions (30 seconds)
      {
        id: 'roomDimensions',
        question: "Great! I need the basic measurements. Can you give me:\n- Linear feet of walls around the perimeter?\n- Ceiling height? (standard 8ft, 9ft, 10ft+, or vaulted)\n- Room length and width for ceiling area?",
        field: 'measurements',
        type: 'text',
        required: true,
        next: () => 'surfaces'
      },
      {
        id: 'houseSize',
        question: "Perfect! How many total rooms are we painting? And what's the approximate square footage of the house? (or tell me: small house under 1500sqft, medium 1500-2500sqft, large 2500sqft+)",
        field: 'houseDetails',
        type: 'text',
        required: true,
        next: () => 'surfaces'
      },
      // Step 2: Surfaces & Scope (45 seconds)
      {
        id: 'surfaces',
        question: "What surfaces are we painting?\n- Walls only?\n- Walls + ceiling?\n- Include trim and doors?\n- Any special features? (accent walls, textured surfaces, high ceilings)",
        field: 'surfaces',
        type: 'text',
        required: true,
        next: (value) => {
          const lower = value.toLowerCase();
          if (lower.includes('trim') || lower.includes('door')) {
            return 'trimDetails';
          }
          return 'paintSelection';
        }
      },
      {
        id: 'trimDetails',
        question: "How many doors and windows need trim work?",
        field: 'trimCount',
        type: 'text',
        required: true,
        next: () => 'paintSelection'
      },
      // Step 3: Paint Selection (20 seconds)
      {
        id: 'paintSelection',
        question: "Which paint are you planning to use?\n- One of your preferred paints?\n- Different paint for walls vs ceiling vs trim?\n- Client has a specific brand request?",
        field: 'paintProducts',
        type: 'text',
        required: true,
        next: () => 'condition'
      },
      // Step 4: Condition Assessment (15 seconds)
      {
        id: 'condition',
        question: "Quick condition check:\n- Walls in good shape or need prep work? (good/minor touch-ups/major prep)\n- Any repairs needed before painting?",
        field: 'prepCondition',
        type: 'select',
        options: ['good', 'minor touch-ups', 'major prep'],
        required: true,
        next: () => 'timeline'
      },
      // Step 5: Timeline & Preferences (10 seconds)
      {
        id: 'timeline',
        question: "When does this need to be completed? (affects scheduling/urgency pricing)\n- This week, next week, within a month, flexible?",
        field: 'timeline',
        type: 'select',
        options: ['this week', 'next week', 'within a month', 'flexible'],
        required: true,
        next: () => 'customerInfo'
      },
      // Quick customer info capture
      {
        id: 'customerInfo',
        question: "Last quick details - customer name and address for the quote?",
        field: 'customerDetails',
        type: 'text',
        required: true,
        next: () => null // End of conversation - ready to generate quote
      }
    ];

    steps.forEach(step => this.steps.set(step.id, step));
  }

  private createInitialState(): ConversationState {
    return {
      currentStep: 'start',
      collectedData: {},
      messages: [],
      isComplete: false,
      quoteReady: false
    };
  }

  reset() {
    this.state = this.createInitialState();
  }

  getCurrentQuestion(): string {
    const step = this.steps.get(this.state.currentStep);
    return step?.question || "I'm having trouble understanding. Could you please rephrase?";
  }

  getCurrentStep(): ConversationStep | undefined {
    return this.steps.get(this.state.currentStep);
  }

  processUserInput(input: string): { 
    response: string; 
    isComplete: boolean;
    collectedData: Record<string, any>;
  } {
    const currentStep = this.steps.get(this.state.currentStep);

    if (!currentStep) {
      return {
        response: "I'm sorry, I got confused. Let's start over.",
        isComplete: false,
        collectedData: this.state.collectedData
      };
    }

    // Add user message to history
    this.state.messages.push({
      role: 'user',
      content: input,
      timestamp: new Date()
    });

    // Process input based on type
    let processedValue: any = input;

    if (currentStep.type === 'number') {
      processedValue = parseFloat(input);
      if (isNaN(processedValue)) {
        return {
          response: "Please provide a valid number.",
          isComplete: false,
          collectedData: this.state.collectedData
        };
      }
    } else if (currentStep.type === 'select') {
      const normalizedInput = input.toLowerCase().trim();
      // Find a match (case-insensitive)
      const matchedOption = currentStep.options?.find(opt => 
        opt.toLowerCase() === normalizedInput || 
        normalizedInput.includes(opt.toLowerCase()) ||
        opt.toLowerCase().includes(normalizedInput)
      );

      if (currentStep.options && !matchedOption) {
        return {
          response: `Please choose one of: ${currentStep.options.join(', ')}`,
          isComplete: false,
          collectedData: this.state.collectedData
        };
      }
      processedValue = matchedOption || normalizedInput;
    } else if (currentStep.type === 'multiselect') {
      // Parse comma-separated values or detect from text
      const items = input.toLowerCase().split(/[,\s]+/).filter(Boolean);
      processedValue = items.filter(item => 
        currentStep.options?.includes(item)
      );

      if (processedValue.length === 0) {
        return {
          response: `Please select from: ${currentStep.options?.join(', ')}`,
          isComplete: false,
          collectedData: this.state.collectedData
        };
      }
    }

    // Validate if needed
    if (currentStep.validation && !currentStep.validation(processedValue)) {
      return {
        response: "That doesn't seem right. Please try again.",
        isComplete: false,
        collectedData: this.state.collectedData
      };
    }

    // Store the collected data
    this.state.collectedData[currentStep.field] = processedValue;

    // Determine next step
    const nextStepId = currentStep.next ? currentStep.next(processedValue) : null;

    if (nextStepId) {
      this.state.currentStep = nextStepId;
      const nextStep = this.steps.get(nextStepId);

      // Add assistant response
      this.state.messages.push({
        role: 'assistant',
        content: nextStep?.question || '',
        timestamp: new Date()
      });

      return {
        response: nextStep?.question || '',
        isComplete: false,
        collectedData: this.state.collectedData
      };
    } else {
      // Conversation complete
      this.state.isComplete = true;
      this.state.quoteReady = true;

      return {
        response: "Perfect! I have all the information I need. Let me generate your quote...",
        isComplete: true,
        collectedData: this.state.collectedData
      };
    }
  }

  getCollectedData(): Record<string, any> {
    return { ...this.state.collectedData };
  }

  getMessages(): Array<{ role: 'user' | 'assistant'; content: string; timestamp: Date }> {
    return [...this.state.messages];
  }

  isComplete(): boolean {
    return this.state.isComplete;
  }

  getState(): any {
    return {
      currentStep: this.state.currentStep,
      isComplete: this.state.isComplete,
      collectedData: this.state.collectedData
    };
  }

  isQuoteReady(): boolean {
    return this.state.quoteReady;
  }

  // Helper to parse contact info
  parseContactInfo(data: Record<string, any>): { email?: string; phone?: string } {
    const contact = data.contactInfo || '';
    const emailMatch = contact.match(/[\w.-]+@[\w.-]+\.\w+/);
    const phoneMatch = contact.match(/[\d\s()+-]+/);

    return {
      email: emailMatch ? emailMatch[0] : undefined,
      phone: phoneMatch ? phoneMatch[0].trim() : undefined
    };
  }

  // Parse measurements from text input
  parseMeasurements(input: string): {
    linearFeetWalls?: number;
    ceilingHeight?: number;
    roomLength?: number;
    roomWidth?: number;
    wallSqft?: number;
    ceilingSqft?: number;
  } {
    const measurements: any = {};

    // Look for linear feet
    const linearMatch = input.match(/(\d+)\s*(?:linear\s*)?(?:feet|ft)/i);
    if (linearMatch) measurements.linearFeetWalls = parseFloat(linearMatch[1]);

    // Look for ceiling height
    const ceilingMatch = input.match(/(\d+)\s*(?:foot|ft|')?\s*(?:ceiling|height)/i);
    if (ceilingMatch) measurements.ceilingHeight = parseFloat(ceilingMatch[1]);

    // Look for room dimensions
    const dimensionMatch = input.match(/(\d+)\s*(?:x|by)\s*(\d+)/i);
    if (dimensionMatch) {
      measurements.roomLength = parseFloat(dimensionMatch[1]);
      measurements.roomWidth = parseFloat(dimensionMatch[2]);
    }

    // Calculate sqft if we have the measurements
    if (measurements.linearFeetWalls && measurements.ceilingHeight) {
      measurements.wallSqft = measurements.linearFeetWalls * measurements.ceilingHeight;
    }
    if (measurements.roomLength && measurements.roomWidth) {
      measurements.ceilingSqft = measurements.roomLength * measurements.roomWidth;
    }

    return measurements;
  }

  // Parse customer details from combined input
  parseCustomerDetails(input: string): {
    customerName?: string;
    address?: string;
  } {
    const lines = input.split(/[,\n]/);
    return {
      customerName: lines[0]?.trim(),
      address: lines.slice(1).join(', ').trim() || undefined
    };
  }

  // Quick quote mode parser - handles comprehensive messages
  parseQuickQuote(input: string): Record<string, any> | null {
    const data: any = {
      measurements: {},
      surfaces: [],
      paintProducts: {}
    };

    // Extract customer name (look for "for [Name] at")
    const customerMatch = input.match(/for\s+(\w+)\s+at/i);
    if (customerMatch) data.customerName = customerMatch[1];

    // Extract address
    const addressMatch = input.match(/at\s+([^.]+?)(?:\.|,|$)/i);
    if (addressMatch) data.address = addressMatch[1].trim();

    // Extract linear feet
    const linearMatch = input.match(/(\d+)\s*(linear\s*)?feet/i);
    if (linearMatch) data.measurements.linearFeetWalls = parseFloat(linearMatch[1]);

    // Extract ceiling height
    const ceilingMatch = input.match(/(\d+)\s*(feet|ft|')\s*(tall|high|ceiling)/i);
    if (ceilingMatch) data.measurements.ceilingHeight = parseFloat(ceilingMatch[1]);

    // Calculate wall sqft
    if (data.measurements.linearFeetWalls && data.measurements.ceilingHeight) {
      data.measurements.wallSqft = data.measurements.linearFeetWalls * data.measurements.ceilingHeight;
    }

    // Extract paint cost
    const costMatch = input.match(/\$(\d+)\s*(a|per)?\s*gallon/i);
    if (costMatch) {
      data.paintProducts.walls = data.paintProducts.walls || {};
      data.paintProducts.walls.costPerGallon = parseFloat(costMatch[1]);
    }

    // Extract coverage rate
    const coverageMatch = input.match(/(\d+)\s*square\s*feet\s*per\s*gallon/i);
    if (coverageMatch) {
      data.paintProducts.walls = data.paintProducts.walls || {};
      data.paintProducts.walls.coverageRate = parseFloat(coverageMatch[1]);
    }

    // Extract paint name (e.g., "eggshell sherwin williams")
    const paintMatch = input.match(/(eggshell|flat|satin|semi-gloss|gloss)\s*([^.,]+)/i);
    if (paintMatch) {
      data.paintProducts.walls = data.paintProducts.walls || {};
      data.paintProducts.walls.name = paintMatch[0].trim();
    }

    // Determine surfaces
    if (/not\s*painting.*ceiling/i.test(input)) {
      data.surfaces.push('walls');
    } else if (/walls?\s*and\s*ceiling/i.test(input)) {
      data.surfaces.push('walls', 'ceiling');
    } else if (/walls?\s*only/i.test(input) || /linear\s*feet.*interior/i.test(input)) {
      data.surfaces.push('walls');
    }

    // Check for trim/doors/windows
    if (!/not\s*painting.*trim/i.test(input) && /trim/i.test(input)) {
      data.surfaces.push('trim');
    }
    if (!/not\s*painting.*door/i.test(input) && /door/i.test(input)) {
      const doorMatch = input.match(/(\d+)\s*doors?/i);
      if (doorMatch) data.measurements.doors = parseInt(doorMatch[1]);
    }
    if (!/not\s*painting.*window/i.test(input) && /window/i.test(input)) {
      const windowMatch = input.match(/(\d+)\s*windows?/i);
      if (windowMatch) data.measurements.windows = parseInt(windowMatch[1]);
    }

    // Default to interior if not specified
    data.projectType = 'interior';

    // Return null if we don't have minimum required data
    if (!data.measurements.wallSqft && !data.measurements.linearFeetWalls) {
      return null;
    }

    return data;
  }
}

import { createClient, SupabaseClient } from '@supabase/supabase-js';
