export class SubscriptionService {
  static async checkQuoteLimit(companyId: number): Promise<{ allowed: boolean; remaining: number; limit: number }> {
    const company = await db.getCompany(companyId);
    if (!company) {
      throw new Error('Company not found');
    }

    // Reset monthly count if needed
    await this.resetMonthlyQuotesIfNeeded(companyId);

    const tier = SUBSCRIPTION_TIERS[company.subscription_tier || 'free'];
    const limit = tier.monthlyQuoteLimit;
    const count = company.monthly_quote_count || 0;

    // Unlimited for pro and enterprise
    if (limit === -1) {
      return { allowed: true, remaining: -1, limit: -1 };
    }

    const remaining = Math.max(0, limit - count);
    return { 
      allowed: count < limit, 
      remaining,
      limit 
    };
  }

  static async incrementQuoteCount(companyId: number): Promise<void> {
    const company = await db.getCompany(companyId);
    if (!company) {
      throw new Error('Company not found');
    }

    const newCount = (company.monthly_quote_count || 0) + 1;
    await db.updateCompany(companyId, {
      monthly_quote_count: newCount
    });
  }

  static async resetMonthlyQuotesIfNeeded(companyId: number): Promise<void> {
    const company = await db.getCompany(companyId);
    if (!company) return;

    const lastReset = company.last_quote_reset ? new Date(company.last_quote_reset) : new Date();
    const now = new Date();

    // Check if we're in a new month
    if (lastReset.getMonth() !== now.getMonth() || lastReset.getFullYear() !== now.getFullYear()) {
      await db.updateCompany(companyId, {
        monthly_quote_count: 0,
        last_quote_reset: now.toISOString()
      });
    }
  }

  static async upgradeToPro(companyId: number, stripeCustomerId?: string, stripeSubscriptionId?: string): Promise<void> {
    const company = await db.getCompany(companyId);
    if (!company) {
      throw new Error('Company not found');
    }

    const previousTier = company.subscription_tier || 'free';

    await db.updateCompany(companyId, {
      subscription_tier: 'pro',
      subscription_status: 'active',
      stripe_customer_id: stripeCustomerId,
      stripe_subscription_id: stripeSubscriptionId,
      trial_ends_at: null // Clear trial if upgrading
    });

    // Log the subscription event
    await this.logSubscriptionEvent(companyId, 'upgrade', previousTier, 'pro');
  }

  static async startTrial(companyId: number, durationDays: number = 14): Promise<Date> {
    const trialEndDate = new Date();
    trialEndDate.setDate(trialEndDate.getDate() + durationDays);

    await db.updateCompany(companyId, {
      subscription_tier: 'pro',
      subscription_status: 'trialing',
      trial_ends_at: trialEndDate.toISOString()
    });

    await this.logSubscriptionEvent(companyId, 'trial_started', 'free', 'pro', { 
      duration_days: durationDays 
    });

    return trialEndDate;
  }

  static async checkTrialStatus(companyId: number): Promise<{ isTrialing: boolean; daysRemaining: number; expired: boolean }> {
    const company = await db.getCompany(companyId);
    if (!company || !company.trial_ends_at) {
      return { isTrialing: false, daysRemaining: 0, expired: false };
    }

    const now = new Date();
    const trialEnd = new Date(company.trial_ends_at);
    const isTrialing = company.subscription_status === 'trialing';
    const daysRemaining = Math.ceil((trialEnd.getTime() - now.getTime()) / (1000 * 60 * 60 * 24));
    const expired = daysRemaining <= 0;

    // If trial expired, downgrade to free
    if (expired && isTrialing) {
      await this.downgradeToFree(companyId);
    }

    return { isTrialing, daysRemaining: Math.max(0, daysRemaining), expired };
  }

  static async downgradeToFree(companyId: number): Promise<void> {
    const company = await db.getCompany(companyId);
    if (!company) return;

    const previousTier = company.subscription_tier || 'pro';

    await db.updateCompany(companyId, {
      subscription_tier: 'free',
      subscription_status: 'active',
      trial_ends_at: null,
      stripe_subscription_id: null
    });

    await this.logSubscriptionEvent(companyId, 'downgrade', previousTier, 'free');
  }

  private static async logSubscriptionEvent(
    companyId: number, 
    eventType: string, 
    fromTier: string, 
    toTier: string,
    metadata?: any
  ): Promise<void> {
    // In a real implementation, this would write to the subscription_events table
    console.log('Subscription event:', {
      companyId,
      eventType,
      fromTier,
      toTier,
      metadata,
      timestamp: new Date().toISOString()
    });
  }

  static async getUsageStats(companyId: number): Promise<{
    currentMonth: {
      quotesCreated: number;
      quotesRemaining: number;
      limit: number;
    };
    lastMonth: {
      quotesCreated: number;
      quotesAccepted: number;
      conversionRate: number;
      avgResponseTime: number;
    };
  }> {
    const company = await db.getCompany(companyId);
    if (!company) {
      throw new Error('Company not found');
    }

    const quoteLimitInfo = await this.checkQuoteLimit(companyId);

    // For last month stats, we'd query the quote_analytics table
    // For now, returning mock data
    return {
      currentMonth: {
        quotesCreated: company.monthly_quote_count || 0,
        quotesRemaining: quoteLimitInfo.remaining,
        limit: quoteLimitInfo.limit
      },
      lastMonth: {
        quotesCreated: 20,
        quotesAccepted: 10,
        conversionRate: 50,
        avgResponseTime: 18 // hours
      }
    };
  }
}

import { stripe, STRIPE_PRICE_IDS } from './stripe-client';
import { getDatabaseAdapter } from '@/lib/database/adapter';
import Stripe from 'stripe';

export type SubscriptionPlan = 'professional' | 'business';
export type BillingPeriod = 'monthly' | 'yearly';

export interface SubscriptionInfo {
  id: string;
  status: string;
  plan: SubscriptionPlan;
  billingPeriod: BillingPeriod;
  currentPeriodStart: Date;
  currentPeriodEnd: Date;
  cancelAtPeriodEnd: boolean;
  customerId: string;
}

export class SubscriptionService {
  private db = getDatabaseAdapter();

  async createCheckoutSession(
    companyId: number,
    plan: SubscriptionPlan,
    billingPeriod: BillingPeriod,
    returnUrl: string
  ): Promise<Stripe.Checkout.Session> {
    const company = await this.db.getCompany(companyId);
    if (!company) {
      throw new Error('Company not found');
    }

    const priceId = STRIPE_PRICE_IDS[plan][billingPeriod];
    if (!priceId) {
      throw new Error('Invalid plan or billing period');
    }

    // Create or retrieve customer
    let customerId = company.stripe_customer_id;
    if (!customerId) {
      const customer = await stripe.customers.create({
        email: company.contact_email,
        name: company.company_name,
        metadata: {
          companyId: companyId.toString()
        }
      });
      customerId = customer.id;

      // Update company with customer ID
      await this.db.updateCompany(companyId, {
        stripe_customer_id: customerId
      });
    }

    const session = await stripe.checkout.sessions.create({
      customer: customerId,
      payment_method_types: ['card'],
      line_items: [
        {
          price: priceId,
          quantity: 1,
        },
      ],
      mode: 'subscription',
      success_url: `${returnUrl}/billing?session_id={CHECKOUT_SESSION_ID}`,
      cancel_url: `${returnUrl}/billing?cancelled=true`,
      metadata: {
        companyId: companyId.toString(),
        plan,
        billingPeriod
      }
    });

    return session;
  }

  async getSubscriptionInfo(companyId: number): Promise<SubscriptionInfo | null> {
    const company = await this.db.getCompany(companyId);
    if (!company?.stripe_customer_id) {
      return null;
    }

    const subscriptions = await stripe.subscriptions.list({
      customer: company.stripe_customer_id,
      status: 'active',
      limit: 1
    });

    const subscription = subscriptions.data[0];
    if (!subscription) {
      return null;
    }

    const price = subscription.items.data[0].price;
    const plan = this.getPlanFromPriceId(price.id);
    const billingPeriod = price.recurring?.interval === 'year' ? 'yearly' : 'monthly';

    return {
      id: subscription.id,
      status: subscription.status,
      plan,
      billingPeriod,
      currentPeriodStart: new Date((subscription as any).current_period_start * 1000),
      currentPeriodEnd: new Date((subscription as any).current_period_end * 1000),
      cancelAtPeriodEnd: (subscription as any).cancel_at_period_end,
      customerId: company.stripe_customer_id
    };
  }

  async cancelSubscription(companyId: number, immediately: boolean = false): Promise<void> {
    const subscriptionInfo = await this.getSubscriptionInfo(companyId);
    if (!subscriptionInfo) {
      throw new Error('No active subscription found');
    }

    if (immediately) {
      await stripe.subscriptions.cancel(subscriptionInfo.id);
    } else {
      await stripe.subscriptions.update(subscriptionInfo.id, {
        cancel_at_period_end: true
      });
    }
  }

  async reactivateSubscription(companyId: number): Promise<void> {
    const subscriptionInfo = await this.getSubscriptionInfo(companyId);
    if (!subscriptionInfo) {
      throw new Error('No subscription found');
    }

    await stripe.subscriptions.update(subscriptionInfo.id, {
      cancel_at_period_end: false
    });
  }

  async createCustomerPortalSession(companyId: number, returnUrl: string): Promise<Stripe.BillingPortal.Session> {
    const company = await this.db.getCompany(companyId);
    if (!company?.stripe_customer_id) {
      throw new Error('No customer ID found');
    }

    return await stripe.billingPortal.sessions.create({
      customer: company.stripe_customer_id,
      return_url: returnUrl,
    });
  }

  async handleWebhook(
    body: string,
    signature: string
  ): Promise<{ processed: boolean; event?: Stripe.Event }> {
    const webhookSecret = process.env.STRIPE_WEBHOOK_SECRET;
    if (!webhookSecret) {
      throw new Error('Stripe webhook secret not configured');
    }

    let event: Stripe.Event;
    try {
      event = stripe.webhooks.constructEvent(body, signature, webhookSecret);
    } catch (err) {
      throw new Error('Invalid webhook signature');
    }

    // Handle the event
    switch (event.type) {
      case 'checkout.session.completed':
        await this.handleCheckoutSessionCompleted(event.data.object as Stripe.Checkout.Session);
        break;
      case 'customer.subscription.created':
      case 'customer.subscription.updated':
        await this.handleSubscriptionUpdated(event.data.object as Stripe.Subscription);
        break;
      case 'customer.subscription.deleted':
        await this.handleSubscriptionDeleted(event.data.object as Stripe.Subscription);
        break;
      case 'invoice.payment_succeeded':
        await this.handleInvoicePaymentSucceeded(event.data.object as Stripe.Invoice);
        break;
      case 'invoice.payment_failed':
        await this.handleInvoicePaymentFailed(event.data.object as Stripe.Invoice);
        break;
      default:
        console.log(`Unhandled event type: ${event.type}`);
        return { processed: false };
    }

    return { processed: true, event };
  }

  private async handleCheckoutSessionCompleted(session: Stripe.Checkout.Session): Promise<void> {
    const companyId = parseInt(session.metadata?.companyId || '0');
    if (!companyId) return;

    // Update company subscription status
    await this.db.updateCompany(companyId, {
      subscription_status: 'active',
      subscription_plan: session.metadata?.plan || 'professional',
      subscription_period_end: new Date(Date.now() + 30 * 24 * 60 * 60 * 1000) // 30 days from now, will be updated by subscription webhook
    });
  }

  private async handleSubscriptionUpdated(subscription: Stripe.Subscription): Promise<void> {
    const companyId = await this.getCompanyIdFromCustomer(subscription.customer as string);
    if (!companyId) return;

    const price = subscription.items.data[0].price;
    const plan = this.getPlanFromPriceId(price.id);

    await this.db.updateCompany(companyId, {
      subscription_status: subscription.status,
      subscription_plan: plan,
      subscription_period_end: new Date((subscription as any).current_period_end * 1000)
    });
  }

  private async handleSubscriptionDeleted(subscription: Stripe.Subscription): Promise<void> {
    const companyId = await this.getCompanyIdFromCustomer(subscription.customer as string);
    if (!companyId) return;

    await this.db.updateCompany(companyId, {
      subscription_status: 'cancelled',
      subscription_plan: null,
      subscription_period_end: new Date((subscription as any).current_period_end * 1000)
    });
  }

  private async handleInvoicePaymentSucceeded(invoice: Stripe.Invoice): Promise<void> {
    const companyId = await this.getCompanyIdFromCustomer(invoice.customer as string);
    if (!companyId) return;

    // Log successful payment
    console.log(`Payment succeeded for company ${companyId}, amount: ${invoice.amount_paid}`);
  }

  private async handleInvoicePaymentFailed(invoice: Stripe.Invoice): Promise<void> {
    const companyId = await this.getCompanyIdFromCustomer(invoice.customer as string);
    if (!companyId) return;

    // Log failed payment - could send notification email here
    console.log(`Payment failed for company ${companyId}, amount: ${invoice.amount_due}`);
  }

  private async getCompanyIdFromCustomer(customerId: string): Promise<number | null> {
    const customer = await stripe.customers.retrieve(customerId);
    if ('metadata' in customer && customer.metadata?.companyId) {
      return parseInt(customer.metadata.companyId);
    }
    return null;
  }

  private getPlanFromPriceId(priceId: string): SubscriptionPlan {
    // Check against our configured price IDs
    if (priceId === STRIPE_PRICE_IDS.professional.monthly || 
        priceId === STRIPE_PRICE_IDS.professional.yearly) {
      return 'professional';
    }
    if (priceId === STRIPE_PRICE_IDS.business.monthly || 
        priceId === STRIPE_PRICE_IDS.business.yearly) {
      return 'business';
    }
    return 'professional'; // Default fallback
  }

  async getUsageStats(companyId: number): Promise<{
    quotesThisMonth: number;
    quotesLimit: number;
    plan: SubscriptionPlan;
  }> {
    const company = await this.db.getCompany(companyId);
    const subscriptionInfo = await this.getSubscriptionInfo(companyId);

    const plan = subscriptionInfo?.plan || 'professional';
    const quotesLimit = plan === 'business' ? -1 : 50; // -1 means unlimited

    // Get quotes created this month
    const startOfMonth = new Date();
    startOfMonth.setDate(1);
    startOfMonth.setHours(0, 0, 0, 0);

    const quotesThisMonth = await this.db.getQuotesCount(companyId, startOfMonth);

    return {
      quotesThisMonth,
      quotesLimit,
      plan
    };
  }
}

